private boolean uploadFile(String actionUrl, File uploadFile) {
  String end = "\r\n";
  String twoHyphens = "--";
  String boundary = "*****";
  try {
    URL url = new URL(actionUrl);
    HttpURLConnection con = (HttpURLConnection) url.openConnection();
    /* InputOutputCache */
    con.setDoInput(true);
    con.setDoOutput(true);
    con.setUseCaches(false);
    /* method=POST */
    con.setRequestMethod("POST");
    /* setRequestProperty */
    con.setRequestProperty("Connection", "Keep-Alive");
    con.setRequestProperty("Charset", "UTF-8");
    con.setRequestProperty("Content-Type", "multipart/form-data;boundary=" + boundary);
    /* DataOutputStream */
    DataOutputStream ds = new DataOutputStream(con.getOutputStream());
    ds.writeBytes(twoHyphens + boundary + end);

    ds.writeBytes(
        "Content-Disposition: form-data; "
            + "name=\"file1\";filename=\""
            + MConstant.USER_ID_VALUE
            + ".jpeg"
            + "\""
            + end);
    ds.writeBytes(end);
    /* FileInputStream */
    FileInputStream fStream = new FileInputStream(uploadFile);
    /* 1024bytes */
    int bufferSize = 1024;
    byte[] buffer = new byte[bufferSize];
    int length = -1;
    /*  */
    while ((length = fStream.read(buffer)) != -1) {
      /* DataOutputStream */
      ds.write(buffer, 0, length);
    }
    ds.writeBytes(end);
    ds.writeBytes(twoHyphens + boundary + twoHyphens + end);
    /* close streams */
    fStream.close();
    ds.flush();
    /* Response */
    InputStream is = con.getInputStream();
    int ch;
    StringBuffer b = new StringBuffer();
    while ((ch = is.read()) != -1) {
      b.append((char) ch);
    }
    // Looper.prepare();
    /* ResponseDialog */
    Log.i("tag", "" + b.toString().trim());
    /* DataOutputStream */
    ds.close();
    return true;
  } catch (Exception e) {
    Log.e("tag", "" + e);
  }
  return false;
}

private void obtainQuestion(String questionBaseURL, QuestionCache.QuestionKey qk)
    throws OmException {
  if (!qc.containsQuestion(qk)) {
