@Override
public Document parse(InputSource source) throws SAXException, IOException {
  if (source == null) {
    throw new IllegalArgumentException("source == null");
  }

  String namespaceURI = null;
  String qualifiedName = null;
  DocumentType doctype = null;
  String inputEncoding = source.getEncoding();
  String systemId = source.getSystemId();
  DocumentImpl document =
      new DocumentImpl(dom, namespaceURI, qualifiedName, doctype, inputEncoding);
  document.setDocumentURI(systemId);

  KXmlParser parser = new KXmlParser();
  try {
    parser.keepNamespaceAttributes();
    parser.setFeature(XmlPullParser.FEATURE_PROCESS_NAMESPACES, namespaceAware);

    if (source.getByteStream() != null) {
      parser.setInput(source.getByteStream(), inputEncoding);
    } else if (source.getCharacterStream() != null) {
      parser.setInput(source.getCharacterStream());
    } else if (systemId != null) {
      URL url = new URL(systemId);
      URLConnection urlConnection = url.openConnection();
      urlConnection.connect();
      // TODO: if null, extract the inputEncoding from the Content-Type header?
      parser.setInput(urlConnection.getInputStream(), inputEncoding);
    } else {
      throw new SAXParseException("InputSource needs a stream, reader or URI", null);
    }

    if (parser.nextToken() == XmlPullParser.END_DOCUMENT) {
      throw new SAXParseException("Unexpected end of document", null);
    }

    parse(parser, document, document, XmlPullParser.END_DOCUMENT);

    parser.require(XmlPullParser.END_DOCUMENT, null, null);
  } catch (XmlPullParserException ex) {
    if (ex.getDetail() instanceof IOException) {
      throw (IOException) ex.getDetail();
    }
    if (ex.getDetail() instanceof RuntimeException) {
      throw (RuntimeException) ex.getDetail();
    }

    LocatorImpl locator = new LocatorImpl();

    locator.setPublicId(source.getPublicId());
    locator.setSystemId(systemId);
    locator.setLineNumber(ex.getLineNumber());
    locator.setColumnNumber(ex.getColumnNumber());

    SAXParseException newEx = new SAXParseException(ex.getMessage(), locator);

    if (errorHandler != null) {
      errorHandler.error(newEx);
    }

    throw newEx;
  } finally {
    IoUtils.closeQuietly(parser);
  }

  return document;
}

private boolean read() {
  try {
    final URLConnection conn = this.url.openConnection();
    conn.setConnectTimeout(5000);

    if (this.apiKey != null) {
      conn.addRequestProperty("X-API-Key", this.apiKey);
    }
    conn.addRequestProperty("User-Agent", "Updater (by Gravity)");

    conn.setDoOutput(true);

    final BufferedReader reader =
        new BufferedReader(new InputStreamReader(conn.getInputStream()));
    final String response = reader.readLine();

    final JSONArray array = (JSONArray) JSONValue.parse(response);

    if (array.size() == 0) {
      this.plugin
          .getLogger()
          .warning("The updater could not find any files for the project id " + this.id);
      this.result = UpdateResult.FAIL_BADID;
      return false;
    }

    this.versionName =
        (String) ((JSONObject) array.get(array.size() - 1)).get(Updater.TITLE_VALUE);
    this.versionLink =
        (String) ((JSONObject) array.get(array.size() - 1)).get(Updater.LINK_VALUE);
    this.versionType =
        (String) ((JSONObject) array.get(array.size() - 1)).get(Updater.TYPE_VALUE);
    this.versionGameVersion =
        (String) ((JSONObject) array.get(array.size() - 1)).get(Updater.VERSION_VALUE);

    return true;
  } catch (final IOException e) {
    if (e.getMessage().contains("HTTP response code: 403")) {
      this.plugin
          .getLogger()
          .warning("dev.bukkit.org rejected the API key provided in plugins/Updater/config.yml");
      this.plugin
          .getLogger()
          .warning("Please double-check your configuration to ensure it is correct.");
      this.result = UpdateResult.FAIL_APIKEY;
    } else {
      this.plugin
          .getLogger()
          .warning("The updater could not contact dev.bukkit.org for updating.");
      this.plugin
          .getLogger()
          .warning(
              "If you have not recently modified your configuration and this is the first time you are seeing this message, the site may be experiencing temporary downtime.");
      this.result = UpdateResult.FAIL_DBO;
    }
    e.printStackTrace();
    return false;
  }
}

