private static void copyFile(URL from, File to) throws IOException {
  synchronized (COPY_FILE_LOCK) {
    URLConnection connection = null;
    InputStream is = null;
    OutputStream os = null;
    try {
      connection = from.openConnection();
      is = connection.getInputStream();
      os = new FileOutputStream(to);
      copy(is, os);
    } finally {
      closeQuietly(os);
      closeQuietly(is);
      if (connection != null && connection instanceof HttpURLConnection) {
        ((HttpURLConnection) connection).disconnect();
      }
    }
  }
}

/**
 * Streams URL content.
 *
 * @param url the URL to stream
 * @param creds credentials for a protected URL
 * @param proxy proxy to use to stream the URL
 * @param proxyCreds credentials to access the proxy
 * @return a URL content InputStream
 */
public static InputStream stream(
    String url, Credentials creds, HttpHost proxy, Credentials proxyCreds) {
  try {
    URLConnection conn;
    if (proxy != null) {
      if (LOG.isDebugEnabled()) {
        LOG.debug("Streaming with proxy: " + proxy.getHostName() + ":" + proxy.getPort());
      }
      Proxy p =
          new Proxy(Proxy.Type.HTTP, new InetSocketAddress(proxy.getHostName(), proxy.getPort()));
      // Authenticator.
      conn = new URL(url).openConnection(p);
      if (proxyCreds != null) {
        LOG.debug("Streaming with proxy credentials.");
        conn.setRequestProperty(
            "Proxy-Authorization",
            base64BasicAuth(proxyCreds.getUsername(), proxyCreds.getPassword()));
      }
    } else {
      conn = new URL(url).openConnection();
    }
    if (creds != null) {
      if (LOG.isDebugEnabled()) {
        LOG.debug("Streaming with credentials.");
      }
      conn.setRequestProperty(
          "Authorization", base64BasicAuth(creds.getUsername(), creds.getPassword()));
    }
    return responseInputStream(conn);
  } catch (IOException e) {
    throw new URLException("Could not stream URL: " + url, e);
  }
}

public void fillStreamFromHttpFile(Uri uri) {
  try {
    URL url = new URL(uri.getPath());
    url.openConnection();
    inStream = new BufferedInputStream(url.openStream());

  } catch (MalformedURLException e) {
    e.printStackTrace();
  } catch (IOException e) {
    e.printStackTrace();
  }
}

/** get URL connection to SifterAPI */
public URLConnection getSifterConnection(String sifterURL) {
  URLConnection sifterConnection = null;
  try {
    // create URL object to SifterAPI
    URL sifter = new URL(sifterURL); // throws MalformedURLException
    // open connection to SifterAPI
    sifterConnection = sifter.openConnection(); // throws IOException
  } catch (MalformedURLException e) {
    e.printStackTrace();
  } catch (IOException e) {
    e.printStackTrace();
  } // catch errors, do nothing, return null, use onConnectionError
  return sifterConnection;
}

/** Does a request to the given URL, reads and returns the result. */
private String request(URL url) throws Exception {
  URLConnection connection = url.openConnection();
  connection.connect();

  InputStream input = connection.getInputStream();
  BufferedReader reader = new BufferedReader(new InputStreamReader(input));
  try {
    return reader.readLine();
  } finally {
    reader.close();
  }
}

@Override
public void createAndconfigureHttpConnection(HttpConfiguration clientConfiguration)
    throws IOException {
  this.config = clientConfiguration;
  URL url = new URL(this.config.getEndPointUrl());
  Proxy proxy = null;
  String proxyHost = this.config.getProxyHost();
  int proxyPort = this.config.getProxyPort();
  if ((proxyHost != null) && (proxyPort > 0)) {
    SocketAddress addr = new InetSocketAddress(proxyHost, proxyPort);
    proxy = new Proxy(Proxy.Type.HTTP, addr);
  }
  if (proxy != null) {
    this.connection = (HttpURLConnection) url.openConnection(proxy);
  } else {
    this.connection = (HttpURLConnection) url.openConnection(Proxy.NO_PROXY);
  }
  if (this.connection instanceof HttpsURLConnection) {
    ((HttpsURLConnection) this.connection)
        .setSSLSocketFactory(this.sslContext.getSocketFactory());
  } else if (this.connection instanceof com.sun.net.ssl.HttpsURLConnection) {
    ((com.sun.net.ssl.HttpsURLConnection) this.connection)
        .setSSLSocketFactory(this.sslContext.getSocketFactory());
  }

  if (this.config.getProxyUserName() != null && this.config.getProxyPassword() != null) {
    final String username = this.config.getProxyUserName();
    final String password = this.config.getProxyPassword();
    Authenticator authenticator = new DefaultPasswordAuthenticator(username, password);
    Authenticator.setDefault(authenticator);
  }

  System.setProperty("http.maxConnections", String.valueOf(this.config.getMaxHttpConnection()));
  System.setProperty("sun.net.http.errorstream.enableBuffering", "true");
  this.connection.setDoInput(true);
  this.connection.setDoOutput(true);
  setRequestMethodViaJreBugWorkaround(this.connection, config.getHttpMethod());
  this.connection.setConnectTimeout(this.config.getConnectionTimeout());
  this.connection.setReadTimeout(this.config.getReadTimeout());
}

/**
 * Returns the raw web stream. Cookies are used to initiate the request, but the cookie jar isn't
 * updated with the received cookies.
 *
 * @param url
 * @param settings
 * @return
 * @throws SocketTimeoutException
 * @throws IOException
 */
public static RawHTTPResponse getWebStream(URL url, RequestSettings requestSettings)
    throws SocketTimeoutException, IOException {
  if (requestSettings == null) {
    requestSettings = new RequestSettings();
  }
  final RequestSettings settings = requestSettings;
  Logger logger = settings.getLogger();
  HTTPMethod method = settings.getMethod();
  Map<String, List<String>> headers = settings.getHeaders();
  Map<String, List<String>> parameters = settings.getParameters();
  Map<String, List<String>> queryParameters = settings.getQueryParameters();
  CookieJar cookieStash = settings.getCookieJar();
  boolean followRedirects = settings.getFollowRedirects();
  final int timeout = settings.getTimeout();
  String username = settings.getUsername();
  String password = settings.getPassword();
  if (logger != null) {
    logger.log(
        Level.INFO,
        "Using the following settings:\n"
            + "HTTP method: {0}\n"
            + "Headers: {1}\n"
            + "Parameters: {2}\n"
            + "Raw parameter Length: {3}\n"
            + "Cookie stash: {4}\n"
            + "Follow redirects? {5}\n"
            + "Timeout: {6}\n"
            + "Username: {7}\n"
            + "Password length: {8}\n",
        new Object[] {
          method,
          headers,
          parameters,
          settings.getRawParameter() == null ? "null" : settings.getRawParameter().length,
          cookieStash,
          followRedirects,
          timeout,
          username,
          password == null ? "null" : password.length()
        });
  }
  // First, let's check to see that the url is properly formatted. If there are parameters,
  // and this is a GET request, we want to tack them on to the end. OR, if there is a raw
  // parameter and parameters,
  // and this is a post reqest, put the parameters on anyways.
  if (parameters != null
      && !parameters.isEmpty()
      && (method == HTTPMethod.GET
          || (method != HTTPMethod.GET
              && settings.getRawParameter() != null
              && settings.getRawParameter().length != 0))) {
    StringBuilder b = new StringBuilder(url.getQuery() == null ? "" : url.getQuery());
    if (b.length() != 0) {
      b.append("&");
    }
    b.append(encodeListParameters(parameters));
    // Setting this to null avoids further processing below
    parameters = null;
    String query = b.toString();
    url = new URL(url.getProtocol(), url.getHost(), url.getPort(), url.getPath() + "?" + query);
  }

  if (queryParameters != null && !queryParameters.isEmpty()) {
    String query = url.getQuery();
    if (query == null) {
      query = "?";
    }
    query += encodeListParameters(queryParameters);
    url = new URL(url.getProtocol(), url.getHost(), url.getPort(), url.getPath() + query);
  }
  if (logger != null) {
    logger.log(Level.INFO, "Using url: {0}", url);
  }

  Proxy proxy;
  if (settings.getProxy() == null) {
    proxy = Proxy.NO_PROXY;
  } else {
    proxy = settings.getProxy();
  }
  if (logger != null) {
    logger.log(Level.INFO, "Using proxy: {0}", proxy);
  }
  InetSocketAddress addr = (InetSocketAddress) proxy.address();
  if (addr != null) {
    if (addr.isUnresolved()) {
      throw new IOException("Could not resolve the proxy address: " + addr.toString());
    }
  }
  if (logger != null) {
    logger.log(Level.INFO, "Opening connection...");
  }
  // FIXME: When given a bad proxy, this causes it to stall forever
  HttpURLConnection conn = (HttpURLConnection) url.openConnection(/*proxy*/ );
  if (conn instanceof HttpsURLConnection
      && (settings.getDisableCertChecking()
          || settings.getUseDefaultTrustStore() == false
          || !settings.getTrustStore().isEmpty())) {
    HttpsURLConnection conns = (HttpsURLConnection) conn;
    // User has requested special handling in the certificates.

    final SSLContext sslc;
    try {
      sslc = SSLContext.getInstance("SSL");
    } catch (NoSuchAlgorithmException ex) {
      throw new IOException(ex);
    }
    TrustManager defaultTrustManager = null;
    {
      if (settings.getUseDefaultTrustStore()) {
        TrustManagerFactory tmf;
        try {
          tmf = TrustManagerFactory.getInstance(TrustManagerFactory.getDefaultAlgorithm());
        } catch (NoSuchAlgorithmException ex) {
          throw new RuntimeException(ex);
        }
        try {
          tmf.init((KeyStore) null);
        } catch (KeyStoreException ex) {
          throw new IOException(ex);
        }
        for (TrustManager tm : tmf.getTrustManagers()) {
          if (tm instanceof X509TrustManager) {
            defaultTrustManager = tm;
            break;
          }
        }
      } else {
        defaultTrustManager = null;
      }
    }
    final X509TrustManager finalDefaultTrustManager = (X509TrustManager) defaultTrustManager;
    final TrustManager[] overrideTrustManager =
        new TrustManager[] {
          new X509TrustManager() {
            @Override
            public void checkClientTrusted(X509Certificate[] xcs, String string)
                throws CertificateException {
              // Hmm. Not sure when this would be used. Always throw for now.
              throw new CertificateException("Not supported yet");
            }

            @Override
            public void checkServerTrusted(X509Certificate[] xcs, String string)
                throws CertificateException {
              if (settings.getDisableCertChecking()) {
                // No cert checking, all pass
                return;
              }
              boolean trusted = true;
              if (finalDefaultTrustManager != null) {
                try {
                  finalDefaultTrustManager.checkClientTrusted(xcs, string);
                } catch (CertificateException ex) {
                  trusted = false;
                }
              }
              if (trusted) {
                return;
              }
              // If any of the certificates are trusted, then the whole chain is trusted
              for (X509Certificate c : xcs) {
                // Unfortunately, we do not know what schemes to use, so we must walk through each
                // trust store item one at a time. We have a documented guarantee that we will
                // walk
                // this list from top to bottom, so we have a linked hash map.
                LinkedHashMap<String, String> ts = settings.getTrustStore();
                for (String fingerprint : ts.keySet()) {
                  fingerprint = fingerprint.toLowerCase().replace(" ", "");
                  try {
                    String scheme = ts.get(fingerprint);
                    String fp = getThumbPrint(c, scheme).toLowerCase().replace(" ", "");
                    if (fp.equals(fingerprint)) {
                      return;
                    }
                  } catch (NoSuchAlgorithmException | CertificateEncodingException ex) {
                    throw new RuntimeException(ex);
                  }
                }
              }
              // None of the certificates matched, so throw an exception
              throw new CertificateException();
            }

            @Override
            public X509Certificate[] getAcceptedIssuers() {
              if (settings.getDisableCertChecking()) {
                return new X509Certificate[0];
              }
              if (finalDefaultTrustManager != null) {
                return finalDefaultTrustManager.getAcceptedIssuers();
              }
              return new X509Certificate[0];
            }
          }
        };
    try {
      sslc.init(null, overrideTrustManager, new java.security.SecureRandom());
    } catch (KeyManagementException ex) {
      throw new IOException(ex);
    }
    final SSLSocketFactory ssf;
    ssf = sslc.getSocketFactory();
    conns.setSSLSocketFactory(
        new SSLSocketFactory() {

          @Override
          public String[] getDefaultCipherSuites() {
            return ssf.getDefaultCipherSuites();
          }

          @Override
          public String[] getSupportedCipherSuites() {
            return ssf.getSupportedCipherSuites();
          }

          @Override
          public Socket createSocket(Socket socket, String string, int i, boolean bln)
              throws IOException {
            return ssf.createSocket(socket, string, i, bln);
          }

          @Override
          public Socket createSocket(String string, int i)
              throws IOException, UnknownHostException {
            return ssf.createSocket(string, i);
          }

          @Override
          public Socket createSocket(String string, int i, InetAddress ia, int i1)
              throws IOException, UnknownHostException {
            return ssf.createSocket(string, i, ia, i1);
          }

          @Override
          public Socket createSocket(InetAddress ia, int i) throws IOException {
            return ssf.createSocket(ia, i);
          }

          @Override
          public Socket createSocket(InetAddress ia, int i, InetAddress ia1, int i1)
              throws IOException {
            return ssf.createSocket(ia, i, ia1, i1);
          }
        });
  }
  conn.setConnectTimeout(timeout);
  conn.setInstanceFollowRedirects(followRedirects);
  if (cookieStash != null) {
    String cookies = cookieStash.getCookies(url);
    if (cookies != null) {
      conn.setRequestProperty("Cookie", cookies);
    }
  }
  if (username != null && password != null) {
    if (logger != null) {
      logger.log(
          Level.INFO, "Using Username/Password authentication, adding Authorization header");
    }
    conn.setRequestProperty(
        "Authorization",
        "Basic "
            + new String(
                Base64.encodeBase64((username + ":" + password).getBytes("UTF-8")), "UTF-8"));
  }
  if (headers != null) {
    for (String key : headers.keySet()) {
      conn.setRequestProperty(key, StringUtils.Join(headers.get(key), ","));
    }
  }
  conn.setRequestMethod(method.name());
  if ((parameters != null && !parameters.isEmpty() && !method.equals(HTTPMethod.GET))
      || settings.getRawParameter() != null) {
    if (logger != null) {
      if (method.equals(HTTPMethod.GET)) {
        logger.log(
            Level.WARNING,
            "Method was set to GET, but raw parameter data was provided, so method"
                + " is changing to POST.");
      }
    }
    conn.setDoOutput(true);
    byte[] params = ArrayUtils.EMPTY_BYTE_ARRAY;
    if (parameters != null && !parameters.isEmpty()) {
      if (logger != null) {
        logger.log(
            Level.INFO,
            "Parameters are added, and content type set to application/x-www-form-urlencoded");
      }
      params = encodeListParameters(parameters).getBytes("UTF-8");
      conn.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
    } else if (settings.getRawParameter() != null) {
      if (logger != null) {
        logger.log(Level.INFO, "Raw parameter is added");
      }
      params = settings.getRawParameter();
    }
    conn.setRequestProperty("Content-Length", Integer.toString(params.length));
    if (logger != null) {
      logger.log(Level.INFO, "Content length is {0}", params.length);
      logger.log(Level.INFO, "Writing out request now");
    }
    OutputStream os = new BufferedOutputStream(conn.getOutputStream());
    os.write(params);
    os.close();
  }
  if (logger != null) {
    logger.log(Level.INFO, "Output sent");
  }
  InputStream is;
  try {
    is = conn.getInputStream();
  } catch (UnknownHostException e) {
    throw e;
  } catch (Exception e) {
    if (logger != null) {
      logger.log(
          Level.SEVERE, "Exception occurred, {0} response from server", conn.getResponseCode());
    }
    if (e instanceof SSLHandshakeException) {
      // The certificate was not valid, and the input stream will be null anyways, so just throw
      // at this
      // point.
      throw new IOException(
          "Invalid SSL certificate for " + url.getHost() + ". Refusing to connect.");
    }
    is = conn.getErrorStream();
  }

  if (!settings.getDisableCompressionHandling() && conn.getContentEncoding() != null) {
    /*
    The HTTP spec for Content-Encoding specifies that multiple comma separated values can be provided. Where
    more than one is provided, this means that the content was compressed multiple times, in the specified order.
    Given that, we must loop through the list, wrapping the input stream in the given decompression handlers.
    In practice, this will only loop once though.
    */
    List<String> compression =
        Stream.of(conn.getContentEncoding().split(","))
            .map((e) -> e.trim())
            .collect(Collectors.toList());
    for (String scheme : compression) {
      if ("x-gzip".equals(scheme) || "gzip".equals(scheme)) {
        if (logger != null) {
          logger.log(Level.INFO, "Response is gzipped, using a GZIPInputStream");
        }
        is = new GZIPInputStream(is);
      } else if ("deflate".equals(scheme)) {
        if (logger != null) {
          logger.log(Level.INFO, "Response is zipped, using an InflaterInputStream");
        }
        is = new InflaterInputStream(is);
      } else if ("br".equals(scheme)) {
        if (logger != null) {
          logger.log(Level.INFO, "Response is Brotli compressed, using a BrotliInputStream");
        }
        is = new BrotliInputStream(is);
      } else if ("identity".equals(scheme)) {
        // This is the default, meaning no transformation is needed.
        if (logger != null) {
          logger.log(Level.INFO, "Response is not compressed");
        }
      }
    }
  }
  if (is == null) {
    throw new IOException("Could not connect to " + url);
  }
  return new RawHTTPResponse(conn, is);
}
