private void createShortcutStation(StationData station) {
  final Intent shortcutIntent = new Intent(Intent.ACTION_MAIN);
  shortcutIntent.setClassName(this, RealtimeView.class.getName());
  shortcutIntent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TOP);

  final Bundle bundle = new Bundle();
  station.writeSimpleBundle(bundle);
  shortcutIntent.putExtras(bundle);

  /*
   * Setup container
   */
  final Intent intent = new Intent();
  intent.putExtra(Intent.EXTRA_SHORTCUT_INTENT, shortcutIntent);
  intent.putExtra(Intent.EXTRA_SHORTCUT_NAME, station.stopName);
  final Parcelable iconResource = Intent.ShortcutIconResource.fromContext(this, R.drawable.icon);
  intent.putExtra(Intent.EXTRA_SHORTCUT_ICON_RESOURCE, iconResource);
  setResult(RESULT_OK, intent);
}

void onReceiveAsync(Context context, Intent intent) {
  setup(context);

  Resources res = mContext.getResources();
  mTips = res.getTextArray(mTipSet == 1 ? R.array.tips2 : R.array.tips);

  if (intent.getAction().equals(ACTION_NEXT_TIP)) {
    mMessage = getNextMessageIndex();
    SharedPreferences.Editor pref = context.getSharedPreferences(PREFS_NAME, 0).edit();
    pref.putInt(PREFS_TIP_NUMBER, mMessage);
    pref.apply();
    refresh();
  } else if (intent.getAction().equals(ACTION_POKE)) {
    blink(intent.getIntExtra(EXTRA_TIMES, 1));
  } else if (intent.getAction().equals(AppWidgetManager.ACTION_APPWIDGET_ENABLED)) {
    goodmorning();
  } else if (intent.getAction().equals("android.provider.Telephony.SECRET_CODE")) {
    Log.d("Protips", "ACHIEVEMENT UNLOCKED");
    mTipSet = 1 - mTipSet;
    mMessage = 0;

    SharedPreferences.Editor pref = context.getSharedPreferences(PREFS_NAME, 0).edit();
    pref.putInt(PREFS_TIP_NUMBER, mMessage);
    pref.putInt(PREFS_TIP_SET, mTipSet);
    pref.apply();

    mContext.startActivity(
        new Intent(Intent.ACTION_MAIN)
            .addFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TOP)
            .addCategory(Intent.CATEGORY_HOME));

    final Intent bcast = new Intent(context, ProtipWidget.class);
    bcast.setAction(ACTION_POKE);
    bcast.putExtra(EXTRA_TIMES, 3);
    mContext.sendBroadcast(bcast);
  } else {
    mIconRes = R.drawable.droidman_open;
    refresh();
  }
}

private Intent getBiometricSensorIntent() {
  Intent fallBackIntent = new Intent().setClass(getActivity(), ChooseLockGeneric.class);
  fallBackIntent.putExtra(LockPatternUtils.LOCKSCREEN_BIOMETRIC_WEAK_FALLBACK, true);
  fallBackIntent.putExtra(CONFIRM_CREDENTIALS, false);
  fallBackIntent.putExtra(
      EXTRA_SHOW_FRAGMENT_TITLE, R.string.backup_lock_settings_picker_title);

  boolean showTutorial =
      ALWAY_SHOW_TUTORIAL || !mChooseLockSettingsHelper.utils().isBiometricWeakEverChosen();
  Intent intent = new Intent();
  intent.setClassName("com.android.facelock", "com.android.facelock.SetupIntro");
  intent.putExtra("showTutorial", showTutorial);
  PendingIntent pending = PendingIntent.getActivity(getActivity(), 0, fallBackIntent, 0);
  intent.putExtra("PendingIntent", pending);
  return intent;
}

/** Creates an Intent from the given 'activityName' and then launches the Intent. */
private void launchActivity() {
  if (callingActivity != null) {
    Intent activityToLaunch = new Intent(actionName);
    activityToLaunch.setComponent(new ComponentName(packageName, activityName));
    activityToLaunch.putExtra("input", input);

    if (isCallable(activityToLaunch)) {
      callingActivity.startActivityForResult(activityToLaunch, REQUEST_CODE);
      launched = true;
      runs++;
    } else {
      Toast.makeText(callingActivity, "Required component is not installed", Toast.LENGTH_SHORT)
          .show();
    }
  } else {
    Log.e(TAG, "The calling Activity was null.");
  }
}

