private void doCrop() {
  final ArrayList<CropOption> cropOptions = new ArrayList<CropOption>();

  Intent intent = new Intent("com.android.camera.action.CROP");
  intent.setType("image/*");

  List<ResolveInfo> list = getPackageManager().queryIntentActivities(intent, 0);

  int size = list.size();

  if (size == 0) {
    Toast.makeText(this, "Can not find image crop app", Toast.LENGTH_SHORT).show();

    return;
  } else {
    intent.setData(mImageCaptureUri);

    intent.putExtra("outputX", 200);
    intent.putExtra("outputY", 200);
    intent.putExtra("aspectX", 1);
    intent.putExtra("aspectY", 1);
    intent.putExtra("scale", true);
    intent.putExtra("return-data", true);

    if (size == 1) {
      Intent i = new Intent(intent);
      ResolveInfo res = list.get(0);

      i.setComponent(new ComponentName(res.activityInfo.packageName, res.activityInfo.name));

      startActivityForResult(i, CROP_FROM_CAMERA);
    } else {
      for (ResolveInfo res : list) {
        final CropOption co = new CropOption();

        co.title = getPackageManager().getApplicationLabel(res.activityInfo.applicationInfo);
        co.icon = getPackageManager().getApplicationIcon(res.activityInfo.applicationInfo);
        co.appIntent = new Intent(intent);

        co.appIntent.setComponent(
            new ComponentName(res.activityInfo.packageName, res.activityInfo.name));

        cropOptions.add(co);
      }

      CropOptionAdapter adapter = new CropOptionAdapter(getApplicationContext(), cropOptions);

      AlertDialog.Builder builder = new AlertDialog.Builder(this);
      builder.setTitle("Choose Crop App");
      builder.setAdapter(
          adapter,
          new DialogInterface.OnClickListener() {
            public void onClick(DialogInterface dialog, int item) {
              startActivityForResult(cropOptions.get(item).appIntent, CROP_FROM_CAMERA);
            }
          });

      builder.setOnCancelListener(
          new DialogInterface.OnCancelListener() {
            @Override
            public void onCancel(DialogInterface dialog) {

              if (mImageCaptureUri != null) {
                getContentResolver().delete(mImageCaptureUri, null, null);
                mImageCaptureUri = null;
              }
            }
          });

      AlertDialog alert = builder.create();

      alert.show();
    }
  }
}

/** Creates an intent that can be sent to this service to join two contacts. */
public static Intent createJoinContactsIntent(
    Context context,
    long contactId1,
    long contactId2,
    boolean contactWritable,
    Class<?> callbackActivity,
    String callbackAction) {
  Intent serviceIntent = new Intent(context, ContactSaveService.class);
  serviceIntent.setAction(ContactSaveService.ACTION_JOIN_CONTACTS);
  serviceIntent.putExtra(ContactSaveService.EXTRA_CONTACT_ID1, contactId1);
  serviceIntent.putExtra(ContactSaveService.EXTRA_CONTACT_ID2, contactId2);
  serviceIntent.putExtra(ContactSaveService.EXTRA_CONTACT_WRITABLE, contactWritable);

  // Callback intent will be invoked by the service once the contacts are joined.
  Intent callbackIntent = new Intent(context, callbackActivity);
  callbackIntent.setAction(callbackAction);
  serviceIntent.putExtra(ContactSaveService.EXTRA_CALLBACK_INTENT, callbackIntent);

  return serviceIntent;
}

/**
 * Used for courier to confirm an order has completed. If order status is incorrect, return -1
 *
 * @param order
 */
public int orderTransactionConfirm(String orderID) {

  // Create a new HttpClient and Post Header
  HttpClient httpclient = new DefaultHttpClient();
  HttpPost httppost = new HttpPost(ORDER_URI);
  ContentValues values = new ContentValues();

  try {
    // Post to server
    List<NameValuePair> nameValuePairs = new ArrayList<NameValuePair>(3);
    nameValuePairs.add(new BasicNameValuePair("orderID", orderID));
    nameValuePairs.add(new BasicNameValuePair("orderStatus", Order.STATUS_CLOSED));
    httppost.setEntity(new UrlEncodedFormEntity(nameValuePairs));
    httpclient.execute(httppost);

    // Update local database
    values.put(MySQLiteHelper.COLUMN_ID, orderID);
    values.put(MySQLiteHelper.COLUMN_ORDERSTATUS, Order.STATUS_CLOSED);
    database.updateWithOnConflict(
        MySQLiteHelper.TABLE_ORDERS, values, null, null, SQLiteDatabase.CONFLICT_IGNORE);

    // Update order list
    Intent intent = new Intent(NEW_INFO_INTENT);
    intent.putExtra(NEW_INFO_EXTRA, NEW_ORDER);
    context.sendBroadcast(intent);
  } catch (ClientProtocolException e) {
    e.printStackTrace();
  } catch (IOException e) {
    e.printStackTrace();
  }
  return 0;
}

final void addContact(
    String[] names,
    String pronunciation,
    String[] phoneNumbers,
    String[] phoneTypes,
    String[] emails,
    String[] emailTypes,
    String note,
    String instantMessenger,
    String address,
    String addressType,
    String org,
    String title,
    String url,
    String birthday) {

  // Only use the first name in the array, if present.
  Intent intent = new Intent(Intent.ACTION_INSERT_OR_EDIT, ContactsContract.Contacts.CONTENT_URI);
  intent.setType(ContactsContract.Contacts.CONTENT_ITEM_TYPE);
  putExtra(intent, ContactsContract.Intents.Insert.NAME, names != null ? names[0] : null);

  putExtra(intent, ContactsContract.Intents.Insert.PHONETIC_NAME, pronunciation);

  int phoneCount =
      Math.min(phoneNumbers != null ? phoneNumbers.length : 0, Contents.PHONE_KEYS.length);
  for (int x = 0; x < phoneCount; x++) {
    putExtra(intent, Contents.PHONE_KEYS[x], phoneNumbers[x]);
    if (phoneTypes != null && x < phoneTypes.length) {
      int type = toPhoneContractType(phoneTypes[x]);
      if (type >= 0) {
        intent.putExtra(Contents.PHONE_TYPE_KEYS[x], type);
      }
    }
  }

  int emailCount = Math.min(emails != null ? emails.length : 0, Contents.EMAIL_KEYS.length);
  for (int x = 0; x < emailCount; x++) {
    putExtra(intent, Contents.EMAIL_KEYS[x], emails[x]);
    if (emailTypes != null && x < emailTypes.length) {
      int type = toEmailContractType(emailTypes[x]);
      if (type >= 0) {
        intent.putExtra(Contents.EMAIL_TYPE_KEYS[x], type);
      }
    }
  }

  // No field for URL, birthday; use notes
  StringBuilder aggregatedNotes = new StringBuilder();
  for (String aNote : new String[] {url, birthday, note}) {
    if (aNote != null) {
      if (aggregatedNotes.length() > 0) {
        aggregatedNotes.append('\n');
      }
      aggregatedNotes.append(aNote);
    }
  }
  if (aggregatedNotes.length() > 0) {
    putExtra(intent, ContactsContract.Intents.Insert.NOTES, aggregatedNotes.toString());
  }

  putExtra(intent, ContactsContract.Intents.Insert.IM_HANDLE, instantMessenger);
  putExtra(intent, ContactsContract.Intents.Insert.POSTAL, address);
  if (addressType != null) {
    int type = toAddressContractType(addressType);
    if (type >= 0) {
      intent.putExtra(ContactsContract.Intents.Insert.POSTAL_TYPE, type);
    }
  }
  putExtra(intent, ContactsContract.Intents.Insert.COMPANY, org);
  putExtra(intent, ContactsContract.Intents.Insert.JOB_TITLE, title);
  launchIntent(intent);
}

