public void showMessageFragment(String phoneNumber) {
  Log.d(TAG, "showMessageFragment ( " + phoneNumber + ")");

  if (isMultiPane()) {
    // Check what fragment is show
    MessageFragment message =
        (MessageFragment) getFragmentManager().findFragmentById(R.id.message);
    if (message == null) {
      // Make a new fragment to show this selection.
      message = MessageFragment.newInstance(phoneNumber);

      // Execute a transaction, replacing any existing fragment with
      // this one inside the frame
      Log.d(TAG, "about to run FragmentTransaction...");
      FragmentTransaction ft = getFragmentManager().beginTransaction();
      ft.setTransition(FragmentTransaction.TRANSIT_FRAGMENT_FADE);
      // ft.addToBackStatc("message");
      ft.replace(R.id.message, message);
      ft.commit();
    }
  } else {
    // In portate mode you need to lauch the messageactivty to display
    // the messageview
    Intent intent = new Intent();
    intent.setClass(this, MessageActivity.class);
    intent.putExtra("phoneNumber", phoneNumber);
    startActivity(intent);
  }
}

@Override
protected void onCreate(Bundle icicle) {
  super.onCreate(icicle);

  Intent i = new Intent(AlarmClock.ACTION_SET_ALARM);
  i.putExtra(AlarmClock.EXTRA_MESSAGE, "New Alarm!");
  i.putExtra(AlarmClock.EXTRA_HOUR, 12);
  i.putExtra(AlarmClock.EXTRA_MINUTES, 27);
  i.putExtra(AlarmClock.EXTRA_SKIP_UI, true);

  startActivity(i);

  // Should not see a duplicate
  startActivity(i);

  i.removeExtra(AlarmClock.EXTRA_MESSAGE);
  startActivity(i);
  // No dup of null message.
  startActivity(i);

  // Launch with SetAlarm UI.
  i.putExtra(AlarmClock.EXTRA_SKIP_UI, false);
  startActivity(i);

  finish();
}

