static Intent registerRunnable(Intent intent, Runnable action) {
  synchronized (ourOnSuccessRunnableMap) {
    if (action != null) {
      ourOnSuccessRunnableMap.put(ourNextCode, action);
      intent.putExtra(RUNNABLE_KEY, ourNextCode);
      ++ourNextCode;
    }
  }
  return intent;
}

