@Override
public boolean dispatchKeyEvent(KeyEvent event) {
  if (event.getKeyCode() == KeyEvent.KEYCODE_BACK && url != null
  /* && !url.contains("login") */ ) {
    if (event.getAction() == KeyEvent.ACTION_DOWN) {
      if ("file:///android_asset/www/com.csair.deviceregist/index.html".equals(url)) {
        if (PadUtils.isPad(application)) {
          Intent i = new Intent(this, FacadeActivity.class);
          i.putExtra("value", URL.PAD_LOGIN_URL);
          i.putExtra("isPad", true);
          this.startActivity(i);
        } else { // 
          Intent i = new Intent(this, FacadeActivity.class);
          i.putExtra("value", URL.PHONE_LOGIN_URL);
          i.putExtra("isPad", false);
          this.startActivity(i);
        }
        return true;
      }
      Dialog dialog =
          new AlertDialog.Builder(this)
              .setTitle("")
              .setMessage(" ")
              .setNegativeButton(
                  "",
                  new DialogInterface.OnClickListener() {
                    @Override
                    public void onClick(DialogInterface dialog, int which) {
                      dialog.dismiss();
                    }
                  })
              .setPositiveButton(
                  "",
                  new DialogInterface.OnClickListener() {

                    @Override
                    public void onClick(DialogInterface dialog, int which) {
                      dialog.dismiss();
                      Application.class
                          .cast(FacadeActivity.this.getApplicationContext())
                          .exitApp();
                      FacadeActivity.this.finish();
                    }
                  })
              .create();

      if (!PadUtils.isPad(this)) {
        if (this.url.endsWith("phone/index.html")) {
          dialog.show();
          return true;
        } else if (this.url.endsWith("#manager")) {
          parentFragment.sendJavascript("backToMain()");
          int index = url.indexOf("#manager");
          url = url.substring(0, index);
          return true;
        } else if (this.url.endsWith("phone/login.html")) {
          dialog.show();
          return true;
        }
      } else {
        dialog.show();
        return true;
      }
    }
  }

  return super.dispatchKeyEvent(event);
}

/**
 * Internal method to handle single sign-on backend for authorize().
 *
 * @param activity The Android Activity that will parent the ProxyAuth Activity.
 * @param applicationId The Facebook application identifier.
 * @param permissions A list of permissions required for this application. If you do not require
 *     any permissions, pass an empty String array.
 * @param activityCode Activity code to uniquely identify the result Intent in the callback.
 */
private boolean startSingleSignOn(
    Activity activity, String applicationId, String[] permissions, int activityCode) {
  boolean didSucceed = true;
  Intent intent = new Intent();

  intent.setClassName("com.facebook.katana", "com.facebook.katana.ProxyAuth");
  intent.putExtra("client_id", applicationId);
  if (permissions.length > 0) {
    intent.putExtra("scope", TextUtils.join(",", permissions));
  }

  // Verify that the application whose package name is
  // com.facebook.katana.ProxyAuth
  // has the expected FB app signature.
  if (!validateActivityIntent(activity, intent)) {
    return false;
  }

  mAuthActivity = activity;
  mAuthPermissions = permissions;
  mAuthActivityCode = activityCode;
  try {
    activity.startActivityForResult(intent, activityCode);
  } catch (ActivityNotFoundException e) {
    didSucceed = false;
  }

  return didSucceed;
}

private void openPlayer(
    int selectedPosition, Context mContext, int videoPostion, boolean isSave) {
  switch (selectedPosition) {
