/**
 * Helper to enables or disables airplane mode. If successful, it also broadcasts an intent
 * indicating that the mode has changed.
 *
 * <p>Note: Needs the following permission: android.permission.WRITE_SETTINGS
 *
 * @param enable true if airplane mode should be ON, false if it should be OFF
 */
protected void setAirplaneModeOn(boolean enable) throws Exception {
  int state = enable ? 1 : 0;

  // Change the system setting
  Settings.System.putInt(mContext.getContentResolver(), Settings.System.AIRPLANE_MODE_ON, state);

  String timeoutMessage =
      "Timed out waiting for airplane mode to be " + (enable ? "enabled!" : "disabled!");

  // wait for airplane mode to change state
  int currentWaitTime = 0;
  while (Settings.System.getInt(
          mContext.getContentResolver(), Settings.System.AIRPLANE_MODE_ON, -1)
      != state) {
    timeoutWait(currentWaitTime, DEFAULT_WAIT_POLL_TIME, DEFAULT_MAX_WAIT_TIME, timeoutMessage);
  }

  // Post the intent
  Intent intent = new Intent(Intent.ACTION_AIRPLANE_MODE_CHANGED);
  intent.putExtra("state", true);
  mContext.sendBroadcast(intent);
}

/**
 * Called when SMS send completes. Broadcasts a sentIntent on success. On failure, either sets up
 * retries or broadcasts a sentIntent with the failure in the result code.
 *
 * @param ar AsyncResult passed into the message handler. ar.result should an SmsResponse instance
 *     if send was successful. ar.userObj should be an SmsTracker instance.
 */
protected void handleSendComplete(AsyncResult ar) {
  SmsTracker tracker = (SmsTracker) ar.userObj;
  PendingIntent sentIntent = tracker.mSentIntent;

  if (ar.result != null) {
    tracker.mMessageRef = ((SmsResponse) ar.result).messageRef;
  } else {
    Rlog.d(TAG, "SmsResponse was null");
  }

  if (ar.exception == null) {
    if (false) {
      Rlog.d(TAG, "SMS send complete. Broadcasting " + "intent: " + sentIntent);
    }

    if (tracker.mDeliveryIntent != null) {
      // Expecting a status report. Add it to the list.
      deliveryPendingList.add(tracker);
    }

    if (sentIntent != null) {
      try {
        if (mRemainingMessages > -1) {
          mRemainingMessages--;
        }

        if (mRemainingMessages == 0) {
          Intent sendNext = new Intent();
          sendNext.putExtra(SEND_NEXT_MSG_EXTRA, true);
          sentIntent.send(mContext, Activity.RESULT_OK, sendNext);
        } else {
          sentIntent.send(Activity.RESULT_OK);
        }
      } catch (CanceledException ex) {
      }
    }
  } else {
    if (false) {
      Rlog.d(TAG, "SMS send failed");
    }

    int ss = mPhone.getServiceState().getState();

    if (ss != ServiceState.STATE_IN_SERVICE) {
      handleNotInService(ss, tracker.mSentIntent);
    } else if ((((CommandException) (ar.exception)).getCommandError()
            == CommandException.Error.SMS_FAIL_RETRY)
        && tracker.mRetryCount < MAX_SEND_RETRIES) {
      // Retry after a delay if needed.
      // TODO: According to TS 23.040, 9.2.3.6, we should resend
      // with the same TP-MR as the failed message, and
      // TP-RD set to 1. However, we don't have a means of
      // knowing the MR for the failed message (EF_SMSstatus
      // may or may not have the MR corresponding to this
      // message, depending on the failure). Also, in some
      // implementations this retry is handled by the baseband.
      tracker.mRetryCount++;
      Message retryMsg = obtainMessage(EVENT_SEND_RETRY, tracker);
      sendMessageDelayed(retryMsg, SEND_RETRY_DELAY);
    } else if (tracker.mSentIntent != null) {
      int error = RESULT_ERROR_GENERIC_FAILURE;

      if (((CommandException) (ar.exception)).getCommandError()
          == CommandException.Error.FDN_CHECK_FAILURE) {
        error = RESULT_ERROR_FDN_CHECK_FAILURE;
      }
      // Done retrying; return an error to the app.
      try {
        Intent fillIn = new Intent();
        if (ar.result != null) {
          fillIn.putExtra("errorCode", ((SmsResponse) ar.result).errorCode);
        }
        if (mRemainingMessages > -1) {
          mRemainingMessages--;
        }

        if (mRemainingMessages == 0) {
          fillIn.putExtra(SEND_NEXT_MSG_EXTRA, true);
        }

        tracker.mSentIntent.send(mContext, error, fillIn);
      } catch (CanceledException ex) {
      }
    }
  }
}

/**
 * Starten der Detail-Ansicht
 *
 * @param markerId
 */
private void startDetailedView(String markerId) {
  byte currentCategId = 0;
  byte currentBuildId = 0;
  int i = 0;
  boolean matchedMarkerFound = false;
  while ((!displayedMarkers.isEmpty())
      && (i < displayedMarkers.size())
      && (!matchedMarkerFound)) { // Suche nach den, zum Marker gehoerigen Daten.
    if (displayedMarkers.get(i).getMarker().getId().equals(markerId)) {
      currentCategId =
          displayedMarkers
              .get(i)
              .getCategId(); // Bestimmung der Kategorie und der Gebaeude-Id des Markers
      currentBuildId = displayedMarkers.get(i).getBuildId();
      matchedMarkerFound = true;
    }
    i++;
  }
  if (matchedMarkerFound) {
    Intent intent =
        new Intent(
            this, DetailActivity.class); // Erstellung eines Intents fuer die Detail-Ansicht.
    intent.putExtra("category", currentCategId); // Uebergabe der Kategorie und Gebaeude-ID.
    intent.putExtra("building", currentBuildId);
    this.startActivity(intent); // Aufruf der Detail-Ansicht.
  }
}

