private void openVideo() {
  if (mUri == null || mSurfaceHolder == null) return;

  Intent i = new Intent("com.android.music.musicservicecommand");
  i.putExtra("command", "pause");
  mContext.sendBroadcast(i);

  release(false);
  try {
    dialog =
        DialogUtil.newLoadingVideoDialog(
            mContext,
            title + this.getResources().getText(com.lorent.video.R.string.loadingvideo),
            this.getResources().getText(com.lorent.video.R.string.pleasewait));
    dialog.show();
    mDuration = -1;
    mCurrentBufferPercentage = 0;
    mMediaPlayer = new MediaPlayer(mContext);
    mMediaPlayer.setOnPreparedListener(mPreparedListener);
    mMediaPlayer.setOnVideoSizeChangedListener(mSizeChangedListener);
    mMediaPlayer.setOnCompletionListener(mCompletionListener);
    mMediaPlayer.setOnErrorListener(mErrorListener);
    mMediaPlayer.setOnBufferingUpdateListener(mBufferingUpdateListener);
    mMediaPlayer.setOnInfoListener(mInfoListener);
    mMediaPlayer.setOnSeekCompleteListener(mSeekCompleteListener);
    mMediaPlayer.setOnSubtitleUpdateListener(mSubtitleUpdateListener);
    mMediaPlayer.setDataSource(mContext, mUri);
    mMediaPlayer.setDisplay(mSurfaceHolder);
    mMediaPlayer.setScreenOnWhilePlaying(true);
    mMediaPlayer.prepareAsync();
    mCurrentState = STATE_PREPARING;
    attachMediaController();
  } catch (IOException ex) {
    Log.e("Unable to open content: " + mUri, ex);
    mCurrentState = STATE_ERROR;
    mTargetState = STATE_ERROR;
    mErrorListener.onError(mMediaPlayer, MediaPlayer.MEDIA_ERROR_UNKNOWN, 0);
    return;
  } catch (IllegalArgumentException ex) {
    Log.e("Unable to open content: " + mUri, ex);
    mCurrentState = STATE_ERROR;
    mTargetState = STATE_ERROR;
    mErrorListener.onError(mMediaPlayer, MediaPlayer.MEDIA_ERROR_UNKNOWN, 0);
    return;
  }
}

