@Override
public void onPerformSync(
    Account account,
    Bundle extras,
    String authority,
    ContentProviderClient provider,
    final SyncResult syncResult) {

  long before = System.currentTimeMillis();
  if (App.DEBUG) Log.v(TAG, "Sync Requested... asking user.");
  Thread.currentThread().setName(TAG);

  mSyncManager = new SyncManager(mContext);

  // ask user to sync
  int count = 0;
  while (!SyncManager.sStartSync.get() && !SyncManager.sCancelSync.get() && count < 20) {
    android.os.SystemClock.sleep(1000);
    count++;
    if (App.DEBUG) Log.v(TAG, "Waiting for User with count=" + count);
  }

  // sync
  if (!SyncManager.sCancelSync.get()) {
    if (App.DEBUG) Log.v(TAG, "Starting an actual sync");
    // Inform Activity
    Intent broadcast = new Intent(SyncManager.SYNC_MESSAGE);
    broadcast.putExtra(SyncManager.START_NEW_SYNC, true);
    LocalBroadcastManager.getInstance(mContext).sendBroadcast(broadcast);

    // Start the Sync
    performSync(syncResult);
  } else {
    if (App.DEBUG) Log.v(TAG, "User cancelled the sync");
  }

  SyncManager.sEndSync.set(true);
  SyncManager.sStartSync.set(false);
  SyncManager.sCancelSync.set(false);

  if (App.DEBUG) Log.v(TAG, "sync is now ending");
  if (App.DEBUG)
    Log.v(
        "SYNC BENCHMARK",
        "Total Sync Time: \n" + sTimeLog.format(new Date(System.currentTimeMillis() - before)));
}

/**
 * Called to let us know the screen was turned off.
 *
 * @param why either {@link WindowManagerPolicy#OFF_BECAUSE_OF_USER}, {@link
 *     WindowManagerPolicy#OFF_BECAUSE_OF_TIMEOUT} or {@link
 *     WindowManagerPolicy#OFF_BECAUSE_OF_PROX_SENSOR}.
 */
public void onScreenTurnedOff(int why) {
  synchronized (this) {
    mScreenOn = false;
    if (DEBUG) Log.d(TAG, "onScreenTurnedOff(" + why + ")");

    if (mExitSecureCallback != null) {
      if (DEBUG) Log.d(TAG, "pending exit secure callback cancelled");
      mExitSecureCallback.onKeyguardExitResult(false);
      mExitSecureCallback = null;
      if (!mExternallyEnabled) {
        hideLocked();
      }
    } else if (mShowing) {
      notifyScreenOffLocked();
      resetStateLocked();
    } else if (why == WindowManagerPolicy.OFF_BECAUSE_OF_TIMEOUT) {
      // if the screen turned off because of timeout, set an alarm
      // to enable it a little bit later (i.e, give the user a chance
      // to turn the screen back on within a certain window without
      // having to unlock the screen)
      long when = SystemClock.elapsedRealtime() + KEYGUARD_DELAY_MS;
      Intent intent = new Intent(DELAYED_KEYGUARD_ACTION);
      intent.putExtra("seq", mDelayedShowingSequence);
      PendingIntent sender =
          PendingIntent.getBroadcast(mContext, 0, intent, PendingIntent.FLAG_CANCEL_CURRENT);
      mAlarmManager.set(AlarmManager.ELAPSED_REALTIME_WAKEUP, when, sender);
      if (DEBUG)
        Log.d(TAG, "setting alarm to turn off keyguard, seq = " + mDelayedShowingSequence);
    } else if (why == WindowManagerPolicy.OFF_BECAUSE_OF_PROX_SENSOR) {
      // Do not enable the keyguard if the prox sensor forced the screen off.
    } else {
      doKeyguard();
    }
  }
}

