/** Called on start up. if safe==false flush cache. set safe to false regardless. */
private void loadState() {
  acquireMetaLock();
  ContentValues data = new ContentValues();
  AcalDBHelper dbHelper = new AcalDBHelper(context);
  SQLiteDatabase db = dbHelper.getWritableDatabase();

  // load start/end range from meta table
  AcalDateTime defaultWindow = new AcalDateTime();
  Cursor mCursor = null;
  try {
    mCursor = db.query(META_TABLE, null, null, null, null, null, null);
  } catch (SQLiteException e) {
    Log.i(TAG, Log.getStackTraceString(e));
    // AcalDBHelper.recoverDatabase(db,true);
    db.close();
    dbHelper.close();
    releaseMetaLock();
    return;
  }
  int closedState = 0;
  try {
    if (mCursor.getCount() < 1) {
      if (CacheManager.DEBUG && Constants.LOG_DEBUG)
        Log.println(Constants.LOGD, TAG, "Initializing cache for first use.");
      data.put(FIELD_CLOSED, 0);
      data.put(FIELD_COUNT, 0);
      data.put(FIELD_START, defaultWindow.getMillis());
      data.put(FIELD_END, defaultWindow.getMillis());
    } else {
      mCursor.moveToFirst();
      DatabaseUtils.cursorRowToContentValues(mCursor, data);
    }
    closedState = data.getAsInteger(FIELD_CLOSED);
  } catch (Exception e) {
    Log.i(TAG, Log.getStackTraceString(e));
  } finally {
    if (mCursor != null) mCursor.close();
  }

  if (!(closedState == 1)) {
    Log.println(
        Constants.LOGI, TAG, "Application not closed correctly last time. Resetting cache.");
    Toast.makeText(
            context,
            "aCal was not correctly shutdown last time.\nRebuilding cache - It may take some time before events are visible.",
            Toast.LENGTH_LONG)
        .show();
    this.CTMinstance.clearCache();
    data.put(FIELD_COUNT, 0);
    data.put(FIELD_START, defaultWindow.getMillis());
    data.put(FIELD_END, defaultWindow.getMillis());
  }
  data.put(FIELD_CLOSED, 1);
  db.delete(META_TABLE, null, null);
  data.remove(FIELD_ID);
  this.metaRow = db.insert(META_TABLE, null, data);
  db.close();
  dbHelper.close();
  long start = data.getAsLong(FIELD_START);
  long end = data.getAsLong(FIELD_END);
  AcalDateRange range = null;
  if (start >= 0 && end >= 0)
    range = new AcalDateRange(AcalDateTime.fromMillis(start), AcalDateTime.fromMillis(end));

  window =
      new CacheWindow(
          lookForward,
          lookBack,
          maxSize,
          minPaddingBack,
          minPaddingForward,
          increment,
          this,
          new AcalDateTime());
  if (range != null) window.setWindowSize(range);

  rm.addListener(this);
  releaseMetaLock();
}

@Override
public InputStream getInputStream(final ITileSource pTileSource, final MapTile pTile) {
  try {
    InputStream ret = null;
    final String[] tile = {COL_TILES_TILE_DATA};
    final String[] xyz = {
      Integer.toString(pTile.getX()),
      Double.toString(
          Math.pow(2, pTile.getZoomLevel()) - pTile.getY() - 1) // Use Google Tiling Spec
      ,
      Integer.toString(pTile.getZoomLevel())
    };

    final Cursor cur =
        mDatabase.query(
            TABLE_TILES,
            tile,
            "tile_column=? and tile_row=? and zoom_level=?",
            xyz,
            null,
            null,
            null);

    if (cur.getCount() != 0) {
      cur.moveToFirst();
      ret = new ByteArrayInputStream(cur.getBlob(0));
    }
    cur.close();
    if (ret != null) {
      return ret;
    }
  } catch (final Throwable e) {
    logger.warn("Error getting db stream: " + pTile, e);
  }

  return null;
}

