private DbMetadata getDbMetadata() {
  DbMetadata dbMetaDta = null;

  final String selectionSql = null;
  final String[] selectionArgs = null;
  final String groupBy = null;
  final String having = null;
  final String orderBy = null;
  final Cursor results =
      db.query(
          DbMetadata.DATABASE_TABLE_NAME,
          null,
          selectionSql,
          selectionArgs,
          groupBy,
          having,
          orderBy);

  if (results.getCount() > 0) {
    results.moveToFirst();
    dbMetaDta = new DbMetadata(results);
  }
  results.close();
  if (dbMetaDta == null) {
    dbMetaDta = new DbMetadata();
    dbMetaDta.lastUpdated = new DateTime().toString(NocturneApplication.simpleDateFmtStrDb);
    db.insert(DbMetadata.DATABASE_TABLE_NAME, null, dbMetaDta.getContentValues());
    NocturneApplication.logMessage(
        Log.DEBUG, LOG_TAG + "getDbMetadata() new metadata object created");
  }
  return dbMetaDta;
}

/**
 * Reads the value of the parameter identified by <code>name</code>
 *
 * @param name
 * @return String value of the parameter, <code>null</code> otherwise
 */
private String getVariable(String name) {
  String[] cols = new String[] {"value"};
  String[] whereArgs = new String[] {name};

  Cursor c = null;

  String result = null;
  try {
    SQLiteDatabase db = this.getReadableDatabase();
    c = db.query(VARIABLE_TABLE_NAME, cols, "name=?", whereArgs, null, null, null);
    boolean hasResult = c.moveToFirst();
    if (hasResult && !c.isNull(0)) {
      result = c.getString(0);
    }
    c.close();
    db.close();
  } catch (Exception e) {
    Log.e(LOG_TAG, "Error getting variable [" + name + "]", e);
  } finally {
    if (null != c) {
      try {
        c.close();
      } catch (Exception e) {
        Log.e(LOG_TAG, "Error closing cursor", e);
      }
    }
  }
  return result;
}

