/**
 * Builds a text representation of the scanning history. Each scan is encoded on one line,
 * terminated by a line break (\r\n). The values in each line are comma-separated, and
 * double-quoted. Double-quotes within values are escaped with a sequence of two double-quotes.
 * The fields output are:
 *
 * <ul>
 *   <li>Raw text
 *   <li>Display text
 *   <li>Format (e.g. QR_CODE)
 *   <li>Timestamp
 *   <li>Formatted version of timestamp
 * </ul>
 */
CharSequence buildHistory() {
  StringBuilder historyText = new StringBuilder(1000);
  SQLiteOpenHelper helper = new DBHelper(activity);
  SQLiteDatabase db = null;
  Cursor cursor = null;
  try {
    db = helper.getWritableDatabase();
    cursor =
        db.query(
            DBHelper.TABLE_NAME,
            COLUMNS,
            null,
            null,
            null,
            null,
            DBHelper.TIMESTAMP_COL + " DESC");

    while (cursor.moveToNext()) {

      historyText.append('"').append(massageHistoryField(cursor.getString(0))).append("\",");
      historyText.append('"').append(massageHistoryField(cursor.getString(1))).append("\",");
      historyText.append('"').append(massageHistoryField(cursor.getString(2))).append("\",");
      historyText.append('"').append(massageHistoryField(cursor.getString(3))).append("\",");

      // Add timestamp again, formatted
      long timestamp = cursor.getLong(3);
      historyText
          .append('"')
          .append(massageHistoryField(EXPORT_DATE_TIME_FORMAT.format(new Date(timestamp))))
          .append("\",");

      // Above we're preserving the old ordering of columns which had formatted data in position 5

      historyText.append('"').append(massageHistoryField(cursor.getString(4))).append("\"\r\n");
    }
    return historyText;
  } finally {
    close(cursor, db);
  }
}

