/**
 * Loads all candidate rows from the name lookup table and updates match scores based on that
 * data.
 */
private void matchAllCandidates(
    SQLiteDatabase db,
    String selection,
    MatchCandidateList candidates,
    ContactMatcher matcher,
    int algorithm,
    String limit) {
  final Cursor c =
      db.query(
          ContactNameLookupQuery.TABLE,
          ContactNameLookupQuery.COLUMNS,
          selection,
          null,
          null,
          null,
          null,
          limit);

  try {
    while (c.moveToNext()) {
      Long contactId = c.getLong(ContactNameLookupQuery.CONTACT_ID);
      String name = c.getString(ContactNameLookupQuery.NORMALIZED_NAME);
      int nameType = c.getInt(ContactNameLookupQuery.NAME_TYPE);

      // Note the N^2 complexity of the following fragment. This is not a huge concern
      // since the number of candidates is very small and in general secondary hits
      // in the absence of primary hits are rare.
      for (int i = 0; i < candidates.mCount; i++) {
        NameMatchCandidate candidate = candidates.mList.get(i);
        matcher.matchName(
            contactId, candidate.mLookupType, candidate.mName, nameType, name, algorithm);
      }
    }
  } finally {
    c.close();
  }
}

