        null);
    contentValues.clear();
  }
  if (!cursor
      .getString(cursor.getColumnIndex(Contract.Messages.COLUMN_NAME_BODY))
      .equals(messageLogRecord.getMessage().getBody())) {
    contentValues.put(
        Contract.Messages.COLUMN_NAME_BODY, messageLogRecord.getMessage().getBody());
    db.update(
        Contract.DATABASE_MESSAGES_TABLE,
        contentValues,
        Contract.Messages._ID + " = " + mId,
        null);
    contentValues.clear();
  }
  if (cursor.getInt(cursor.getColumnIndex(Contract.Messages.COLUMN_NAME_CODE))
