public String[] getAvailableWeaponTypes(
    FFXIDAO dao, SQLiteDatabase db, int part, int race, int job, int level, String filter) {
  Cursor cursor;
  String partStr, jobStr, alljobStr;
  String filterexp;
  String result[];
  String columns[] = {C_Weapon};

  partStr = dao.getString(FFXIString.PART_DB_MAIN + part);
  jobStr = dao.getString(FFXIString.JOB_DB_WAR + job);
  alljobStr = dao.getString(FFXIString.JOB_DB_ALL);
  filterexp = "";
  if (filter.length() > 0) {
    filterexp =
        " AND ("
            + C_Name
            + " LIKE '%"
            + filter
            + "%' OR "
            + C_Description
            + " LIKE '%"
            + filter
            + "%' OR "
            + C_Augment
            + " LIKE '%"
            + filter
            + "%')";
  }

  result = null;
  try {
    cursor =
        db.query(
            TABLE_NAME,
            columns,
            C_Part
                + " LIKE '%"
                + partStr
                + "%' AND "
                + C_Level
                + " <= '"
                + level
                + "' AND "
                + "("
                + C_Job
                + " LIKE '%"
                + jobStr
                + "%' OR "
                + C_Job
                + " = '"
                + alljobStr
                + "')"
                + filterexp,
            null,
            C_Weapon,
            null,
            C_Weapon);

    if (cursor.getCount() > 0) {
      result = new String[cursor.getCount()];

      cursor.moveToFirst();
      for (int i = 0; i < result.length; i++) {
        result[i] = cursor.getString(cursor.getColumnIndex(C_Weapon));
        cursor.moveToNext();
      }
    }

    cursor.close();
  } catch (SQLiteException e) {
  }

  return result;
}

