/** @see StringKeyValueStore#keyValuePairs() */
public Enumeration keyValuePairs() {

  if (dbStore == null) {
    open();
  }

  final Cursor result =
      dbStore.query(
          true,
          tableName,
          QUERY_KEY_VALUE_COLUMN,
          null,
          null,
          null,
          null,
          KEY_COLUMN_NAME + " ASC",
          null);

  final int keyColumnIndex = result.getColumnIndexOrThrow(KEY_COLUMN_NAME);
  final int valueColumnIndex = result.getColumnIndexOrThrow(VALUE_COLUMN_NAME);

  // Move Cursor to the first element
  result.moveToFirst();

  return new Enumeration() {

    boolean last = false;
    boolean closed = false;

    public Object nextElement() {

      // Get the Current value
      String key = result.getString(keyColumnIndex);
      String value = result.getString(valueColumnIndex);

      // Move Cursor to the next element
      result.moveToNext();

      return new StringKeyValuePair(key, value);
    }

    public boolean hasMoreElements() {
      if (last) {
        return false;
      }
      last = result.isAfterLast();
      if (last) {
        result.close();
        closed = true;
      }
      return !last;
    }

    public void finalize() throws Throwable {
      try {
        if (!closed) {
          result.close();
          closed = true;
        }
      } finally {
        super.finalize();
      }
    }
  };
}

public Cursor query(String table) {
  SQLiteDatabase db = getReadableDatabase();
  return db.query(table, null, null, null, null, null, null);
}

private synchronized void reloadAccounts(final SQLiteDatabase db) {

  final String[] fields =
      new String[] {FIELD_USERNAME, FIELD_COOKIES, FIELD_MODHASH, FIELD_PRIORITY};

  final Cursor cursor = db.query(TABLE, fields, null, null, null, null, FIELD_PRIORITY + " ASC");

  accountsCache = new LinkedList<RedditAccount>();
  defaultAccountCache = null;

  // TODO handle null? can this even happen?
  if (cursor != null) {

    while (cursor.moveToNext()) {

      final String username = cursor.getString(0);
      final byte[] cookies = cursor.getBlob(1);
      final String modhash = cursor.getString(2);
      final long priority = cursor.getLong(3);

      final RedditAccount account;

      try {
        account =
            new RedditAccount(
                username,
                modhash,
                cookies == null ? null : new PersistentCookieStore(cookies),
                priority);

      } catch (IOException e) {
        BugReportActivity.handleGlobalError(context, new RRError(null, null, e));
        return;
      }

      accountsCache.add(account);

      if (defaultAccountCache == null || account.priority < defaultAccountCache.priority) {
        defaultAccountCache = account;
      }
    }

    cursor.close();

  } else {
    BugReportActivity.handleGlobalError(context, "Cursor was null after query");
  }
}

private void assertTableExists(SQLiteDatabase db, String table) {
  assertNotNull("Missing table: " + table, db.query(table, null, null, null, null, null, null));
}

public void trimHistory() {
  SQLiteOpenHelper helper = new DBHelper(activity);
  SQLiteDatabase db = null;
  Cursor cursor = null;
  try {
    db = helper.getWritableDatabase();
    cursor =
        db.query(
            DBHelper.TABLE_NAME,
            ID_COL_PROJECTION,
            null,
            null,
            null,
            null,
            DBHelper.TIMESTAMP_COL + " DESC");
    cursor.move(MAX_ITEMS);
    while (cursor.moveToNext()) {
      db.delete(DBHelper.TABLE_NAME, DBHelper.ID_COL + '=' + cursor.getString(0), null);
    }
  } catch (SQLiteException sqle) {
    // We're seeing an error here when called in CaptureActivity.onCreate() in rare cases
    // and don't understand it. First theory is that it's transient so can be safely ignored.
    // TODO revisit this after live in a future version to see if it 'worked'
    Log.w(TAG, sqle);
    // continue
  } finally {
    close(cursor, db);
  }
}

public boolean hasHistoryItems() {
  SQLiteOpenHelper helper = new DBHelper(activity);
  SQLiteDatabase db = null;
  Cursor cursor = null;
  try {
    db = helper.getReadableDatabase();
    cursor = db.query(DBHelper.TABLE_NAME, COUNT_COLUMN, null, null, null, null, null);
    cursor.moveToFirst();
    return cursor.getInt(0) > 0;
  } finally {
    close(cursor, db);
  }
}
