/**
 * Test the block operations of the RandomAccessFile class. These are the methods that read/write
 * blocks of data. The following checks are made:
 *
 * <ul>
 *   <li>Writing blocks of data that are smaller than the buffer size.
 *   <li>Writing blocks of data that are larger than the buffer size.
 *   <li>Checking the size of the file is correct.
 *   <li>Reading small blocks of the file back in and verifying its contents.
 *   <li>Reading large blocks of the file back in and verifying its contents.
 * </ul>
 *
 * @param filename the name of the test file to generate.
 */
public static void testBlocks(String filename) {

  System.err.println("\nTesting block operations...");

  // Generate the data.
  int bufferSize = 10;
  byte data[] = new byte[256];
  for (int i = 0; i < data.length; i++) {
    data[i] = (byte) (i % 256);
  }

  try {

    // Write the data in small and large blocks.
    RandomAccessFile outFile =
        new RandomAccessFile(
            filename, RandomAccessFile.WRITE | RandomAccessFile.CREATE, bufferSize);
    for (int i = 0; i < data.length; ) {
      int blockSize = (i < data.length / 2) ? 3 : 13;
      blockSize = (i + blockSize >= data.length) ? (data.length - i) : blockSize;
      outFile.write(data, i, blockSize);
      i += blockSize;
    }

    outFile.close();

    // Check that the file length is correct.
    if ((new File(filename)).length() != data.length) {
      System.out.println(
          "X New file size incorrect (should be "
              + data.length
              + ", but is "
              + (new File(filename)).length()
              + ").");
    } else {
      System.out.println(". File size correct (" + data.length + ").");
    }

    // Reopen the file for reading.
    RandomAccessFile inFile = new RandomAccessFile(filename, RandomAccessFile.READ, bufferSize);

    // Read and check random small blocks of data.
    boolean verified = true;
    int firstFailure = 256;
    Random random = new Random(0);
    byte block[] = new byte[(int) (bufferSize * 0.5)];
    for (int i = 0; i < 100; i++) {
      int index = Math.abs(random.nextInt()) % (data.length - block.length);
      inFile.seek(index);
      inFile.read(block);

      // Verify the block of data.
      for (int j = 0; j < block.length; j++) {
        if (block[j] != data[index + j]) {
          verified = false;
          if (index + j < firstFailure) {
            firstFailure = index + j;
          }
        }
      }
    }
    if (verified) {
      System.err.println(". Reading small blocks verified.");
    } else {
      System.err.println("X Reading small blocks failed (byte " + firstFailure + ").");
    }

    // Read and check random large (bigger than the bufferSize) blocks
    // of data.
    verified = true;
    random = new Random(0);
    block = new byte[(int) (bufferSize * 1.5)];
    for (int i = 0; i < 100; i++) {
      int index = Math.abs(random.nextInt()) % (data.length - block.length);
      inFile.seek(index);
      inFile.read(block);

      // Verify the block of data.
      for (int j = 0; j < block.length; j++) {
        if (block[j] != data[j + index]) {
          verified = false;
        }
      }
    }
    if (verified) {
      System.err.println(". Reading large blocks verified.");
    } else {
      System.err.println("X Reading large blocks failed.");
    }

    // Close the input file.
    inFile.close();

  } catch (Exception e) {
    e.printStackTrace();
  }
}

/**
 * Tries to open the input file as a Zip archive (jar/apk) with a "classes.dex" inside.
 *
 * @return a RandomAccessFile for classes.dex, or null if the input file is not a zip archive
 * @throws IOException if the file isn't found, or it's a zip and classes.dex isn't found inside
 */
RandomAccessFile openInputFileAsZip() throws IOException {
  ZipFile zipFile;

  /*
   * Try it as a zip file.
   */
  try {
    zipFile = new ZipFile(mInputFileName);
  } catch (FileNotFoundException fnfe) {
    /* not found, no point in retrying as non-zip */
    System.err.println("Unable to open '" + mInputFileName + "': " + fnfe.getMessage());
    throw fnfe;
  } catch (ZipException ze) {
    /* not a zip */
    return null;
  }

  /*
   * We know it's a zip; see if there's anything useful inside. A
   * failure here results in some type of IOException (of which
   * ZipException is a subclass).
   */
  ZipEntry entry = zipFile.getEntry(CLASSES_DEX);
  if (entry == null) {
    System.err.println("Unable to find '" + CLASSES_DEX + "' in '" + mInputFileName + "'");
    zipFile.close();
    throw new ZipException();
  }

  InputStream zis = zipFile.getInputStream(entry);

  /*
   * Create a temp file to hold the DEX data, open it, and delete it
   * to ensure it doesn't hang around if we fail.
   */
  File tempFile = File.createTempFile("dexdeps", ".dex");
  // System.out.println("+++ using temp " + tempFile);
  RandomAccessFile raf = new RandomAccessFile(tempFile, "rw");
  tempFile.delete();

  /*
   * Copy all data from input stream to output file.
   */
  byte copyBuf[] = new byte[32768];
  int actual;

  while (true) {
    actual = zis.read(copyBuf);
    if (actual == -1) break;

    raf.write(copyBuf, 0, actual);
  }

  zis.close();
  raf.seek(0);

  return raf;
}

