 */
public void write(RandomAccessFile file) throws java.io.IOException {
  int size = 0;
  int offset = 0;
  byte[] buffer = new byte[5];
  String str = "";

  size = getSize();
  str = Integer.toString(size);

  for (int i = 0; i < (5 - str.length()); i++) {
    buffer[i] = (byte) '0';
  }

  offset += (5 - str.length());

  for (int i = 0; i < str.length(); i++) {
    buffer[i + offset] = (byte) str.charAt(i);
  }

  offset += str.length();
  file.write(buffer, 0, 5);

  if (size > 0) {
    str = writeString();
    buffer = new byte[str.length()];

    for (int i = 0; i < str.length(); i++) {
      buffer[i] = (byte) str.charAt(i);
    }

    file.write(buffer);
  }
}

/** Close Riff File. Length is written too. */
public int Close() {
  int retcode = DDC_SUCCESS;

  switch (fmode) {
    case RFM_WRITE:
      try {
        file.seek(0);
        try {
          byte[] br = new byte[8];
          br[0] = (byte) ((riff_header.ckID >>> 24) & 0x000000FF);
          br[1] = (byte) ((riff_header.ckID >>> 16) & 0x000000FF);
          br[2] = (byte) ((riff_header.ckID >>> 8) & 0x000000FF);
          br[3] = (byte) (riff_header.ckID & 0x000000FF);

          br[7] = (byte) ((riff_header.ckSize >>> 24) & 0x000000FF);
          br[6] = (byte) ((riff_header.ckSize >>> 16) & 0x000000FF);
          br[5] = (byte) ((riff_header.ckSize >>> 8) & 0x000000FF);
          br[4] = (byte) (riff_header.ckSize & 0x000000FF);
          file.write(br, 0, 8);
          file.close();
        } catch (IOException ioe) {
          retcode = DDC_FILE_ERROR;
        }
      } catch (IOException ioe) {
        retcode = DDC_FILE_ERROR;
      }
      break;

    case RFM_READ:
      try {
        file.close();
      } catch (IOException ioe) {
        retcode = DDC_FILE_ERROR;
      }
      break;
  }
  file = null;
  fmode = RFM_UNKNOWN;
  return retcode;
}

private void parseBody(Map<String, String> files) throws IOException, ResponseException {
  RandomAccessFile randomAccessFile = null;
  BufferedReader in = null;
  try {

    randomAccessFile = getTmpBucket();

    long size;
    if (headers.containsKey("content-length")) {
      size = Integer.parseInt(headers.get("content-length"));
    } else if (splitbyte < rlen) {
      size = rlen - splitbyte;
    } else {
      size = 0;
    }

    // Now read all the body and write it to f
    byte[] buf = new byte[512];
    while (rlen >= 0 && size > 0) {
      rlen = inputStream.read(buf, 0, 512);
      size -= rlen;
      if (rlen > 0) {
        randomAccessFile.write(buf, 0, rlen);
      }
    }

    // Get the raw body as a byte []
    ByteBuffer fbuf =
        randomAccessFile
            .getChannel()
            .map(FileChannel.MapMode.READ_ONLY, 0, randomAccessFile.length());
    randomAccessFile.seek(0);

    // Create a BufferedReader for easily reading it as string.
    InputStream bin = new FileInputStream(randomAccessFile.getFD());
    in = new BufferedReader(new InputStreamReader(bin));

    // If the method is POST, there may be parameters
    // in data section, too, read it:
    if (Method.POST.equals(method)) {
      String contentType = "";
      String contentTypeHeader = headers.get("content-type");

      StringTokenizer st = null;
      if (contentTypeHeader != null) {
        st = new StringTokenizer(contentTypeHeader, ",; ");
        if (st.hasMoreTokens()) {
          contentType = st.nextToken();
        }
      }

      if ("multipart/form-data".equalsIgnoreCase(contentType)) {
        // Handle multipart/form-data
        if (!st.hasMoreTokens()) {
          throw new ResponseException(
              Response.Status.BAD_REQUEST,
              "BAD REQUEST: Content type is multipart/form-data but boundary missing. Usage: GET /example/file.html");
        }

        String boundaryStartString = "boundary=";
        int boundaryContentStart =
            contentTypeHeader.indexOf(boundaryStartString) + boundaryStartString.length();
        String boundary =
            contentTypeHeader.substring(boundaryContentStart, contentTypeHeader.length());
        if (boundary.startsWith("\"") && boundary.startsWith("\"")) {
          boundary = boundary.substring(1, boundary.length() - 1);
        }

        decodeMultipartData(boundary, fbuf, in, parms, files);
      } else {
        // Handle application/x-www-form-urlencoded
        String postLine = "";
        char pbuf[] = new char[512];
        int read = in.read(pbuf);
        while (read >= 0 && !postLine.endsWith("\r\n")) {
          postLine += String.valueOf(pbuf, 0, read);
          read = in.read(pbuf);
        }
        postLine = postLine.trim();
        decodeParms(postLine, parms);
      }
    } else if (Method.PUT.equals(method)) {
      files.put("content", saveTmpFile(fbuf, 0, fbuf.limit()));
    }
  } finally {
    safeClose(randomAccessFile);
    safeClose(in);
  }
}

