private void writeWhole(final Deflater def, final int typeCode, final byte[] data)
    throws IOException {
  int sz = data.length;
  int hdrlen = 0;
  buf[hdrlen++] = (byte) ((typeCode << 4) | sz & 15);
  sz >>>= 4;
  while (sz > 0) {
    buf[hdrlen - 1] |= 0x80;
    buf[hdrlen++] = (byte) (sz & 0x7f);
    sz >>>= 7;
  }
  packDigest.update(buf, 0, hdrlen);
  crc.update(buf, 0, hdrlen);
  packOut.write(buf, 0, hdrlen);
  def.reset();
  def.setInput(data);
  def.finish();
  while (!def.finished()) {
    final int datlen = def.deflate(buf);
    packDigest.update(buf, 0, datlen);
    crc.update(buf, 0, datlen);
    packOut.write(buf, 0, datlen);
  }
}

/**
 * Write RGB data. Each 3-byte triplet in the bitmap array represents blue, green, and red,
 * respectively, for a pixel. The color bytes are in reverse order (Windows convention). Lines are
 * padded to a length that is a multiple of 4 bytes.
 */
private void writeRGBFrame(ImageProcessor ip) throws IOException {
  ip = ip.convertToRGB();
  int[] pixels = (int[]) ip.getPixels();
  int width = ip.getWidth();
  int height = ip.getHeight();
  int c, offset, index = 0;
  for (int y = height - 1; y >= 0; y--) {
    offset = y * width;
    for (int x = 0; x < width; x++) {
      c = pixels[offset++];
      bufferWrite[index++] = (byte) (c & 0xff); // blue
      bufferWrite[index++] = (byte) ((c & 0xff00) >> 8); // green
      bufferWrite[index++] = (byte) ((c & 0xff0000) >> 16); // red
    }
    for (int i = 0; i < linePad; i++) bufferWrite[index++] = (byte) 0;
  }
  raFile.write(bufferWrite);
}

