private synchronized void buildIndexes() {
  int nMin = 0x7fffffff;
  int nMax = 0x80000000;

  FileSystem.createPath(m_csDir);
  String tcsNames[] = FileSystem.getFileNameListBySuffix(m_csDir, ".q");
  if (tcsNames == null) nMin = nMax = 0;
  else if (tcsNames.length == 0) nMin = nMax = 0;
  else {
    for (int n = 0; n < tcsNames.length; n++) {
      String csFullName = tcsNames[n];
      String csName = FileSystem.getNameWithoutExtension(csFullName);
      int nName = NumberParser.getAsInt(csName);
      if (nName < nMin) nMin = nName;
      if (nName > nMax) nMax = nName;
    }
    nMax++;
  }

  RandomAccessFile fileIndex;
  try {
    fileIndex = new RandomAccessFile(m_csIndexFile, "rw");
    String csLine = "" + nMin + ":" + nMax;

    byte tb[] = csLine.getBytes();
    fileIndex.seek(0);

    fileIndex.write(tb);
    fileIndex.setLength(csLine.length());
    fileIndex.close();
  } catch (FileNotFoundException e) {
    // TODO Auto-generated catch block
    e.printStackTrace();
  } catch (IOException e) {
    // TODO Auto-generated catch block
    e.printStackTrace();
  }
}

public void put(long key, byte[] data, long timestamp) {
  // Check to see if the record already exists.
  Record record = null;
  synchronized (mIndexMap) {
    record = mIndexMap.get(key);
  }
  if (record != null && data.length <= record.sizeOnDisk) {
    // We just replace the chunk.
    int currentChunk = record.chunk;
    try {
      RandomAccessFile chunkFile = getChunkFile(record.chunk);
      if (chunkFile != null) {
        chunkFile.seek(record.offset);
        chunkFile.write(data);
        synchronized (mIndexMap) {
          mIndexMap.put(
              key,
              new Record(currentChunk, record.offset, data.length, record.sizeOnDisk, timestamp));
        }
        if (++mNumInsertions == 32) { // CR: 32 => constant
          // Flush the index file at a regular interval. To avoid
          // writing the entire
          // index each time the format could be changed to an
          // append-only journal with
          // a snapshot generated on exit.
          flush();
        }
        return;
      }
    } catch (Exception e) {
      Log.e(TAG, "Unable to read from chunk file");
    }
  }
  // Append a new chunk to the current chunk.
  final int chunk = mTailChunk;
  final RandomAccessFile chunkFile = getChunkFile(chunk);
  if (chunkFile != null) {
    try {
      final int offset = (int) chunkFile.length();
      chunkFile.seek(offset);
      chunkFile.write(data);
      synchronized (mIndexMap) {
        mIndexMap.put(key, new Record(chunk, offset, data.length, data.length, timestamp));
      }
      if (offset + data.length > CHUNK_SIZE) {
        ++mTailChunk;
      }

      if (++mNumInsertions == 32) { // CR: 32 => constant
        // Flush the index file at a regular interval. To avoid
        // writing the entire
        // index each time the format could be changed to an
        // append-only journal with
        // a snapshot generated on exit.
        flush();
      }
    } catch (IOException e) {
      Log.e(TAG, "Unable to write new entry to chunk file");
    }
  } else {
    Log.e(TAG, "getChunkFile() returned null");
  }
}

/**
 * Write the rest of the data in an input stream to disk. Note: this method does not close the
 * InputStream.
 *
 * @param in open stream containing the data to write to the disk cache.
 * @return a token to retrieve the data later
 */
public synchronized long transferFromStream(InputStream in) throws IOException {
  assert in != null;
  byte[] buf = Util.takeThreadLocalBuf();
  try {
    long position = moveToEndPosition();

    // Placeholder, we don't know the length yet.
    file.writeInt(-1);

    // Transfer all the bytes.
    int length = 0;
    int bytesRead;
    while ((bytesRead = in.read(buf)) != -1) {
      file.write(buf, 0, bytesRead);
      length += bytesRead;
    }

    // Now go back and fill in the length.
    file.seek(position);
    file.writeInt(length);
    // Don't eagerly seek the end, the next operation might be a read.
    atEnd = false;
    return position;
  } finally {
    Util.releaseThreadLocalBuf(buf);
  }
}

/**
 * Write the color table entries (for 8 bit grayscale or indexed color). Byte order or LUT
 * entries: blue byte, green byte, red byte, 0 byte
 */
private void writeLUT(ImageProcessor ip) throws IOException {
  IndexColorModel cm = (IndexColorModel) (ip.getCurrentColorModel());
  int mapSize = cm.getMapSize();
  byte[] lutWrite = new byte[4 * 256];
  for (int i = 0; i < 256; i++) {
    if (i < mapSize) {
      lutWrite[4 * i] = (byte) cm.getBlue(i);
      lutWrite[4 * i + 1] = (byte) cm.getGreen(i);
      lutWrite[4 * i + 2] = (byte) cm.getRed(i);
      lutWrite[4 * i + 3] = (byte) 0;
    }
  }
  raFile.write(lutWrite);
}

@Override
public Bitmap doInBackground(Object... params) {
  Bitmap bitmap;
  try {
    int readLength;
    cachePath = KKImageManager.getTempImagePath(context, url);
    File cacheFile = new File(cachePath);
    File localFile = null;
    String tempFilePath =
        context.getCacheDir().getAbsolutePath()
            + File.separator
            + "image"
            + File.separator
            + hashCode();
    if (localPath != null) {
      localFile = new File(localPath);
    }
    try {
      if (cacheFile.exists()) {
        if (actionType == KKImageManager.ActionType.DOWNLOAD) {
          if (localFile == null || !localFile.exists()) {
            cryptToFile(cachePath, localPath);
          }
          return null;
        } else {
          bitmap = decodeBitmap(cachePath);
          if (bitmap != null) {
            if (localPath != null && saveToLocal && (localFile == null || !localFile.exists())) {
              cryptToFile(cachePath, localPath);
            }
            return bitmap;
          } else {
            removeCacheFile();
          }
        }
      }
      if (localFile != null && localFile.exists()) {
        if (actionType == KKImageManager.ActionType.DOWNLOAD) {
          return null;
        } else {
          cryptToFile(localPath, tempFilePath);
          moveFileTo(tempFilePath, cachePath);
          bitmap = decodeBitmap(cachePath);
          if (bitmap != null) {
            return bitmap;
          } else {
            removeCacheFile();
          }
        }
      }
    } catch (Exception e) {
    }
    // Do fetch server resource if either cache nor local file is not valid to read
    if (!KKImageManager.networkEnabled) {
      return null;
    }
    response = httpClient.newCall(new Request.Builder().url(url).build()).execute();
    if (!response.isSuccessful()) {
      throw new Exception("Unexpected code " + response);
    }
    final InputStream is = response.body().byteStream();
    headers = response.headers();
    removeInvalidImageFiles();
    if (actionType == KKImageManager.ActionType.DOWNLOAD) {
      RandomAccessFile tempFile = new RandomAccessFile(tempFilePath, "rw");
      while ((readLength = is.read(buffer, 0, buffer.length)) != -1) {
        if (interuptFlag) {
          return null;
        }
        if (cipher != null) {
          buffer = cipher.doFinal(buffer);
        }
        tempFile.write(buffer, 0, readLength);
      }
      tempFile.close();
      moveFileTo(tempFilePath, localPath);
      return null;
    } else {
      RandomAccessFile tempFile;
      try {
        tempFile = new RandomAccessFile(tempFilePath, "rw");
      } catch (IOException e) {
        // we don't save to SD card if cache is full
        return BitmapFactory.decodeStream(is);
      }
      try {
        while ((readLength = is.read(buffer, 0, buffer.length)) != -1) {
          if (interuptFlag) {
            return null;
          }
          tempFile.write(buffer, 0, readLength);
        }
      } catch (IOException e) {
        tempFile.close();
        return null;
      }
      tempFile.close();
      moveFileTo(tempFilePath, cachePath);
      bitmap = decodeBitmap(cachePath);
      if (bitmap != null) {
        if (saveToLocal && localPath != null) {
          cryptToFile(cachePath, localPath);
        }
        return bitmap;
      }
    }
  } catch (final Exception e) {
    isNetworkError = true;
    removeInvalidImageFiles();
  }
  return null;
}

/**
 * Write an signed short even though parameter is an int. Like java.io.RandomAcessFile#writeShort.
 * also acts as a writeUnsignedShort.
 *
 * @param v signed number to write
 * @throws IOException if read fails.
 */
public final void writeShort(int v) throws IOException {
  work[0] = (byte) v;
  work[1] = (byte) (v >> 8);
  raf.write(work, 0, 2);
}

