  }
}

/**
 * Check a source code file. The following properties are checked: copyright, license, incorrect
 * source switches, trailing white space, newline characters, tab characters, and characters codes
 * (only characters below 128 are allowed).
 *
 * @param file the file to check
 * @param fix automatically fix newline characters and trailing spaces
 * @param checkLicense check the license and copyright
 */
public void checkOrFixFile(File file, boolean fix, boolean checkLicense) throws Exception {
  RandomAccessFile in = new RandomAccessFile(file, "r");
  byte[] data = new byte[(int) file.length()];
  ByteArrayOutputStream out = fix ? new ByteArrayOutputStream() : null;
  in.readFully(data);
  in.close();
  if (checkLicense) {
    if (data.length > COPYRIGHT.length() + LICENSE.length()) {
      // don't check tiny files
      String text = new String(data);
      if (text.indexOf(COPYRIGHT) < 0) {
        fail(file, "copyright is missing", 0);
      }
      if (text.indexOf(LICENSE) < 0) {
        fail(file, "license is missing", 0);
      }
      if (text.indexOf("// " + "##") > 0) {
        fail(file, "unexpected space between // and ##", 0);
      }
      if (text.indexOf("/* " + "##") > 0) {
        fail(file, "unexpected space between /* and ##", 0);
      }
      if (text.indexOf("##" + " */") > 0) {
        fail(file, "unexpected space between ## and */", 0);
      }
    }
  }
  int line = 1;
  boolean lastWasWhitespace = false;
  for (int i = 0; i < data.length; i++) {
    char ch = (char) (data[i] & 0xff);
    boolean isWhitespace = Character.isWhitespace(ch);
    if (ch > 127) {
      fail(file, "contains character " + (int) ch + " at " + new String(data, i - 10, 20), line);
      return;
    } else if (ch < 32) {
      if (ch == '\n') {
        if (lastWasWhitespace && !ALLOW_TRAILING_SPACES) {
          fail(file, "contains trailing white space", line);
          return;
        }
        if (fix) {
          if (useCRLF) {
            out.write('\r');
          }
          out.write(ch);
        }
        lastWasWhitespace = false;
        line++;
      } else if (ch == '\r') {
        if (!ALLOW_CR) {
          fail(file, "contains CR", line);
          return;
        }
        if (lastWasWhitespace && !ALLOW_TRAILING_SPACES) {
          fail(file, "contains trailing white space", line);
          return;
        }
        lastWasWhitespace = false;
        // ok
      } else if (ch == '\t') {
        if (fix) {
          for (int j = 0; j < SPACES_PER_TAB; j++) {
            out.write(' ');
          }
        } else {
          if (!ALLOW_TAB) {
            fail(file, "contains TAB", line);
            return;
          }
        }
        lastWasWhitespace = true;
        // ok
      } else {
        fail(file, "contains character " + (int) ch, line);
        return;
      }
    } else if (isWhitespace) {
      lastWasWhitespace = true;
      if (fix) {
        boolean write = true;
        for (int j = i + 1; j < data.length; j++) {
          char ch2 = (char) (data[j] & 0xff);
          if (ch2 == '\n' || ch2 == '\r') {
            write = false;
            lastWasWhitespace = false;
            ch = ch2;
            i = j - 1;
            break;
          } else if (!Character.isWhitespace(ch2)) {
            break;
          }
        }
        if (write) {
          out.write(ch);
        }
      }
    } else {
      if (fix) {
        out.write(ch);
      }
      lastWasWhitespace = false;
    }
  }
  if (lastWasWhitespace && !ALLOW_TRAILING_SPACES) {
    fail(file, "contains trailing white space at the very end", line);
    return;
  }
  if (fix) {
    byte[] changed = out.toByteArray();
    if (!Arrays.equals(data, changed)) {
      RandomAccessFile f = new RandomAccessFile(file, "rw");
      f.write(changed);
      f.setLength(changed.length);
      f.close();
      System.out.println("CHANGED: " + file.getName());
    }
  }
  line = 1;
  for (int i = 0; i < data.length; i++) {
    if (data[i] < 32) {
      line++;
      for (int j = i + 1; j < data.length; j++) {
        if (data[j] != 32) {
          int mod = (j - i - 1) & 3;
          if (mod != 0 && (mod != 1 || data[j] != '*')) {
            fail(file, "contains wrong number of heading spaces: " + (j - i - 1), line);
          }
          break;
        }
      }
    }
  }
}

@Override
protected void onEndThinPack() throws IOException {
  final byte[] tailHash = this.tailDigest.digest();
  final byte[] buf = buffer();

  final MessageDigest origDigest = Constants.newMessageDigest();
  final MessageDigest tailDigest = Constants.newMessageDigest();
  final MessageDigest packDigest = Constants.newMessageDigest();

  long origRemaining = origEnd;
  out.seek(0);
  out.readFully(buf, 0, 12);
  origDigest.update(buf, 0, 12);
  origRemaining -= 12;

  NB.encodeInt32(buf, 8, getObjectCount());
  out.seek(0);
  out.write(buf, 0, 12);
  packDigest.update(buf, 0, 12);

  for (; ; ) {
    final int n = out.read(buf);
    if (n < 0) break;
    if (origRemaining != 0) {
      final int origCnt = (int) Math.min(n, origRemaining);
      origDigest.update(buf, 0, origCnt);
      origRemaining -= origCnt;
      if (origRemaining == 0) tailDigest.update(buf, origCnt, n - origCnt);
    } else tailDigest.update(buf, 0, n);

    packDigest.update(buf, 0, n);
  }

  if (!Arrays.equals(origDigest.digest(), origHash)
      || !Arrays.equals(tailDigest.digest(), tailHash))
    throw new IOException(JGitText.get().packCorruptedWhileWritingToFilesystem);

  packHash = packDigest.digest();
}

