  }
}

/**
 * Handle a partial PUT. New content specified in request is appended to existing content in
 * oldRevisionContent (if present). This code does not support simultaneous partial updates to the
 * same resource.
 */
protected File executePartialPut(HttpServletRequest req, Range range, String path)
    throws IOException {

  // Append data specified in ranges to existing content for this
  // resource - create a temp. file on the local filesystem to
  // perform this operation
  File tempDir = (File) getServletContext().getAttribute(ServletContext.TEMPDIR);
  // Convert all '/' characters to '.' in resourcePath
  String convertedResourcePath = path.replace('/', '.');
  File contentFile = new File(tempDir, convertedResourcePath);
  if (contentFile.createNewFile()) {
    // Clean up contentFile when Tomcat is terminated
    contentFile.deleteOnExit();
  }

  RandomAccessFile randAccessContentFile = new RandomAccessFile(contentFile, "rw");

  Resource oldResource = null;
  try {
    Object obj = resources.lookup(path);
    if (obj instanceof Resource) oldResource = (Resource) obj;
  } catch (NamingException e) {
    // Ignore
  }

  // Copy data in oldRevisionContent to contentFile
  if (oldResource != null) {
    BufferedInputStream bufOldRevStream =
        new BufferedInputStream(oldResource.streamContent(), BUFFER_SIZE);

    int numBytesRead;
    byte[] copyBuffer = new byte[BUFFER_SIZE];
    while ((numBytesRead = bufOldRevStream.read(copyBuffer)) != -1) {
      randAccessContentFile.write(copyBuffer, 0, numBytesRead);
    }

    bufOldRevStream.close();
  }

  randAccessContentFile.setLength(range.length);

  // Append data in request input stream to contentFile
  randAccessContentFile.seek(range.start);
  int numBytesRead;
  byte[] transferBuffer = new byte[BUFFER_SIZE];
  BufferedInputStream requestBufInStream =
      new BufferedInputStream(req.getInputStream(), BUFFER_SIZE);
  while ((numBytesRead = requestBufInStream.read(transferBuffer)) != -1) {
    randAccessContentFile.write(transferBuffer, 0, numBytesRead);
  }
  randAccessContentFile.close();
  requestBufInStream.close();

  return contentFile;
}

