/**
 * write the data into cache.
 *
 * <p>The API saves <code>len</code> bytes in <code>b</code> from <code>off</code> to block <code>
 * blockId</code> from <code>blockOff</code>
 *
 * @param blockId block id.
 * @param blockOff offset in the block.
 * @param b data to be saved
 * @param off offset.
 * @param len write size.
 * @throws IOException
 */
synchronized void write(int blockId, int blockOff, byte[] b, int off, int len)
    throws IOException {
  assertWritable();
  ensureFileCreated();
  long pos = (long) blockId * BLOCK_SIZE + blockOff;
  rf.seek(pos);
  rf.write(b, off, len);
}

private void checkFileCorruption(LocalFileSystem fileSys, Path file, Path fileToCorrupt)
    throws IOException {

  // corrupt the file
  RandomAccessFile out = new RandomAccessFile(new File(fileToCorrupt.toString()), "rw");

  byte[] buf = new byte[(int) fileSys.getFileStatus(file).getLen()];
  int corruptFileLen = (int) fileSys.getFileStatus(fileToCorrupt).getLen();
  assertTrue(buf.length >= corruptFileLen);

  rand.nextBytes(buf);
  out.seek(corruptFileLen / 2);
  out.write(buf, 0, corruptFileLen / 4);
  out.close();

  boolean gotException = false;

  InputStream in = fileSys.open(file);
  try {
    IOUtils.readFully(in, buf, 0, buf.length);
  } catch (ChecksumException e) {
    gotException = true;
  }
  assertTrue(gotException);
  in.close();
}

public void record() {
  audioRecorder.read(buffer, 0, buffer.length); // Fill buffer
  try {
    // System.out.println("BUFFERSTR: " + buffer.length);
    randomAccessWriter.write(buffer); // Write buffer to file
    payloadSize += buffer.length;
    if (bSamples == 16) {
      for (int i = 0; i < buffer.length / 2; i++) { // 16bit sample size
        short curSample = getShort(buffer[i * 2], buffer[i * 2 + 1]);
        if (curSample > cAmplitude) { // Check amplitude
          cAmplitude = curSample;
        }
      }
    } else { // 8bit sample size
      for (int i = 0; i < buffer.length; i++) {
        if (buffer[i] > cAmplitude) { // Check amplitude
          cAmplitude = buffer[i];
        }
      }
    }
  } catch (IOException e) {
    e.printStackTrace();
    Log.e(
        ExtAudioRecorder.class.getName(),
        "Error occured in updateListener, recording is aborted");
    // stop();
  }
}

