public void write(RandomAccessFile file) throws IOException {
  int offset = 0;
  String str;
  byte[] buffer = new byte[5];

  str = Integer.toString(value.length);

  for (int i = 0; i < (5 - str.length()); i++) {
    buffer[i] = (byte) '0';
  }

  offset += (5 - str.length());

  for (int i = 0; i < str.length(); i++) {
    buffer[i + offset] = (byte) str.charAt(i);
  }

  file.write(buffer);

  file.write(value);
}

/**
 * Checks the size of the index and data files, growing them to at least initial capacity or
 * GROWTH_FACTOR times larger
 */
private void checkCapacities() throws IOException {
  long posI = indexFile.getFilePointer();
  long lenI = indexFile.length();
  if (posI >= (0.8 * lenI)) {
    indexFile.seek(lenI);
    long newSize = ((long) (lenI * GROWTH_FACTOR)) + lenI;
    if (newSize == 0) newSize = INITIAL_CAPACITY * SIZEOF_INDEX_ENTRY;
    indexFile.setLength(newSize);
    long count = (newSize - lenI) / SIZEOF_INDEX_ENTRY / 1024;

    // 16k buffer, 1024 entries of 16 bytes
    ByteArrayOutputStream baos = new ByteArrayOutputStream();
    DataOutputStream dos = new DataOutputStream(baos);
    for (int x = 0; x < 1024; x++) {
      dos.writeLong(-1);
      dos.writeLong(-1);
    }
    byte[] ba = baos.toByteArray();
    while (count-- > 0) {
      indexFile.write(ba);
    }
    indexFile.seek(posI);
  }

  long posD = dataFile.getFilePointer();
  long lenD = dataFile.length();
  if (posD >= (0.8 * lenD)) {
    long newSize = ((long) (lenD * GROWTH_FACTOR)) + lenD;
    if (newSize == 0) newSize = INITIAL_CAPACITY * 1024; // 1k per record
    dataFile.setLength(newSize);
    dataFile.seek(posD);
  }
}

public void erase(int size, int offset) throws IOException {
  byte[] bytes = new byte[256];
  int chunkSize;

  while (size > 0) {
    chunkSize = size > bytes.length ? bytes.length : size;
    imageFile.seek(conf.startOffset + offset);
    imageFile.write(bytes, 0, chunkSize);
    size -= chunkSize;
    offset += chunkSize;
  }
}

private void insertInternal(long key, byte[] data, int length) throws IOException {
  byte[] header = mBlobHeader;
  int sum = checkSum(data);
  writeLong(header, BH_KEY, key);
  writeInt(header, BH_CHECKSUM, sum);
  writeInt(header, BH_OFFSET, mActiveBytes);
  writeInt(header, BH_LENGTH, length);
  mActiveDataFile.write(header);
  mActiveDataFile.write(data, 0, length);

  mIndexBuffer.putLong(mSlotOffset, key);
  mIndexBuffer.putInt(mSlotOffset + 8, mActiveBytes);
  mActiveBytes += BLOB_HEADER_SIZE + length;
  writeInt(mIndexHeader, IH_ACTIVE_BYTES, mActiveBytes);
}

/** Write data from the current buffer position to the payload offset with the length */
protected void internal_write(IByteBuffer bp, int offset, int len) {
  assert lock_.isHeldByCurrentThread()
      : "BundlePayload:internal_write, lock not held by current Thread";
  assert length_ >= (offset + len)
      : "BundlePayload:internal_write, length_ is not longer than offset + len";

  byte[] temp = new byte[len];
  switch (location_) {
    case MEMORY:
      bp.mark();
      try {
        for (int i = offset; i < offset + len; i++) {
          data_[i] = bp.get();
        }
      } finally {
        bp.reset();
      }
      break;
    case DISK:
      // check if we need to seek
      bp.mark();
      try {

        RandomAccessFile file_handle_ = pin_file_handle();
        bp.get(temp);
        file_handle_.seek(offset);

        file_handle_.write(temp);

        unpin_file_handle(file_handle_);
      } catch (BundlePayloadWrongTypeException e) {

        BPF.getInstance().getBPFLogger().error(TAG, e.getMessage());
      } catch (IOException e) {
        BPF.getInstance().getBPFLogger().error(TAG, e.getMessage());
      } finally {
        bp.reset();
      }
      break;
    case NODATA:
  }
}

/**
 * Opens the incremental update log file in read/write mode. If the file does not exist, it is
 * created.
 *
 * @throws IOException if an I/O error occurs
 */
private void openLogFile() throws IOException {
  try {
    close();
  } catch (IOException e) {
    /* assume this is okay */
  }

  logName = versionName(logfilePrefix);
  log = new RandomAccessFile(logName, "rw");
  logFD = log.getFD();

  if (logBytes == 0) {
    format = FORMAT_PADDED;
    writeInt(log, MAGIC);
    writeInt(log, format);
    logBytes = (intBytes + intBytes);
  } else {
    log.seek(logBytes);
  }
  log.setLength(logBytes);
  /* always start out with zero length header for the next update */
  log.write(zeroBuf);
  /* force length header to disk */
  logFD.sync();
}

protected static void write32(RandomAccessFile out, long l) throws IOException {
  out.write((byte) ((l >> 24) & 0xff));
  out.write((byte) ((l >> 16) & 0xff));
  out.write((byte) ((l >> 8) & 0xff));
  out.write((byte) ((l >> 0) & 0xff));
}

public void delete(final RandomAccessFile file) throws IOException {
  // this works by just erasing the "TAG" tag at the beginning
  // of the file
  final byte[] buffer = new byte[3];
  if (seek(file)) {
    file.seek(0);
    file.write(buffer);
  }
}

