/** Puts items into the store. */
protected synchronized void store(Object element, Locale locale) throws IOException {
  if (!isInitalized) return;
  // Serialise the entry
  final ByteArrayOutputStream outstr = new ByteArrayOutputStream();
  final ObjectOutputStream objstr = new ObjectOutputStream(outstr);
  objstr.writeObject(element);
  objstr.close();
  final byte[] buffer = outstr.toByteArray();

  // check if there is space
  // if (diskElements.size() >= maxDataSize)
  // {
  // return false;
  // }

  // Check for a free block
  DiskElement diskElement = findFreeBlock(buffer.length);
  if (diskElement == null) {
    diskElement = new DiskElement();
    diskElement.position = randomAccessFile.length();
    diskElement.blockSize = buffer.length;
  }

  // TODO - cleanup block on failure
  // Write the record
  randomAccessFile.seek(diskElement.position);

  // TODO the free block algorithm will gradually leak disk space, due to
  // payload size being less than block size
  // this will be a problem for the persistent cache
  randomAccessFile.write(buffer);

  // Add to index, update stats
  diskElement.payloadSize = buffer.length;
  totalSize += buffer.length;

  // create the localized buffer
  if (!diskElements.containsKey(locale)) {

    diskElements.put(locale, new LinkedList());
  }
  ((LinkedList) diskElements.get(locale)).addLast(diskElement);

  if (log.isDebugEnabled()) {
    long menUsed = Runtime.getRuntime().totalMemory() - Runtime.getRuntime().freeMemory();
    log.debug(
        "Store "
            + locale.toString()
            + " on object, total size : "
            + size()
            + " Total unsed elements : "
            + freeSpace.size()
            + " memory used "
            + menUsed);
  }
}

void fillO(long offset) throws IOException {
  raf.seek(offset);
  int remainder = vsize;
  for (; remainder >= fillbytes.length; remainder -= fillbytes.length) raf.write(fillbytes);
  // handle any remainder;
  if (remainder > 0) for (int ii = 0; ii < remainder; ii++) raf.write(fillbytes[ii]);
}

@Test
public void testPendingReplicationRetry() throws IOException {

  MiniDFSCluster cluster = null;
  int numDataNodes = 4;
  String testFile = "/replication-test-file";
  Path testPath = new Path(testFile);

  byte buffer[] = new byte[1024];
  for (int i = 0; i < buffer.length; i++) {
    buffer[i] = '1';
  }

  try {
    Configuration conf = new HdfsConfiguration();
    conf.set(DFSConfigKeys.DFS_REPLICATION_KEY, Integer.toString(numDataNodes));
    // first time format
    cluster = new MiniDFSCluster.Builder(conf).numDataNodes(numDataNodes).build();
    cluster.waitActive();
    DFSClient dfsClient =
        new DFSClient(new InetSocketAddress("localhost", cluster.getNameNodePort()), conf);

    OutputStream out = cluster.getFileSystem().create(testPath);
    out.write(buffer);
    out.close();

    waitForBlockReplication(testFile, dfsClient.getNamenode(), numDataNodes, -1);

    // get first block of the file.
    ExtendedBlock block =
        dfsClient.getNamenode().getBlockLocations(testFile, 0, Long.MAX_VALUE).get(0).getBlock();

    cluster.shutdown();
    cluster = null;

    for (int i = 0; i < 25; i++) {
      buffer[i] = '0';
    }

    int fileCount = 0;
    // Choose 3 copies of block file - delete 1 and corrupt the remaining 2
    for (int dnIndex = 0; dnIndex < 3; dnIndex++) {
      File blockFile = MiniDFSCluster.getBlockFile(dnIndex, block);
      LOG.info("Checking for file " + blockFile);

      if (blockFile != null && blockFile.exists()) {
        if (fileCount == 0) {
          LOG.info("Deleting file " + blockFile);
          assertTrue(blockFile.delete());
        } else {
          // corrupt it.
          LOG.info("Corrupting file " + blockFile);
          long len = blockFile.length();
          assertTrue(len > 50);
          RandomAccessFile blockOut = new RandomAccessFile(blockFile, "rw");
          try {
            blockOut.seek(len / 3);
            blockOut.write(buffer, 0, 25);
          } finally {
            blockOut.close();
          }
        }
        fileCount++;
      }
    }
    assertEquals(3, fileCount);

    /* Start the MiniDFSCluster with more datanodes since once a writeBlock
     * to a datanode node fails, same block can not be written to it
     * immediately. In our case some replication attempts will fail.
     */

    LOG.info("Restarting minicluster after deleting a replica and corrupting 2 crcs");
    conf = new HdfsConfiguration();
    conf.set(DFSConfigKeys.DFS_REPLICATION_KEY, Integer.toString(numDataNodes));
    conf.set(DFSConfigKeys.DFS_NAMENODE_REPLICATION_PENDING_TIMEOUT_SEC_KEY, Integer.toString(2));
    conf.set("dfs.datanode.block.write.timeout.sec", Integer.toString(5));
    conf.set(
        DFSConfigKeys.DFS_NAMENODE_SAFEMODE_THRESHOLD_PCT_KEY, "0.75f"); // only 3 copies exist

    cluster =
        new MiniDFSCluster.Builder(conf).numDataNodes(numDataNodes * 2).format(false).build();
    cluster.waitActive();

    dfsClient =
        new DFSClient(new InetSocketAddress("localhost", cluster.getNameNodePort()), conf);

    waitForBlockReplication(testFile, dfsClient.getNamenode(), numDataNodes, -1);

  } finally {
    if (cluster != null) {
      cluster.shutdown();
    }
  }
}
