@Override
public void breakBlock(World par1World, int par2, int par3, int par4, int par5, int par6) {
  TileEntityLChest TileEntityLChest =
      (TileEntityLChest) par1World.getBlockTileEntity(par2, par3, par4);

  if (TileEntityLChest != null) {
    for (int j1 = 0; j1 < TileEntityLChest.getSizeInventory(); ++j1) {
      ItemStack itemstack = TileEntityLChest.getStackInSlot(j1);

      if (itemstack != null) {
        float f = this.random.nextFloat() * 0.8F + 0.1F;
        float f1 = this.random.nextFloat() * 0.8F + 0.1F;
        EntityItem entityitem;

        for (float f2 = this.random.nextFloat() * 0.8F + 0.1F;
            itemstack.stackSize > 0;
            par1World.spawnEntityInWorld(entityitem)) {
          int k1 = this.random.nextInt(21) + 10;

          if (k1 > itemstack.stackSize) {
            k1 = itemstack.stackSize;
          }

          itemstack.stackSize -= k1;
          entityitem =
              new EntityItem(
                  par1World,
                  par2 + f,
                  par3 + f1,
                  par4 + f2,
                  new ItemStack(itemstack.itemID, k1, itemstack.getItemDamage()));
          float f3 = 0.05F;
          entityitem.motionX = (float) this.random.nextGaussian() * f3;
          entityitem.motionY = (float) this.random.nextGaussian() * f3 + 0.2F;
          entityitem.motionZ = (float) this.random.nextGaussian() * f3;

          if (itemstack.hasTagCompound()) {
            entityitem
                .getEntityItem()
                .setTagCompound((NBTTagCompound) itemstack.getTagCompound().copy());
          }
        }
      }
    }

    par1World.func_96440_m(par2, par3, par4, par5);
  }

  super.breakBlock(par1World, par2, par3, par4, par5, par6);
}

