public ClusterModel createClusterModel(ExampleSet es) throws OperatorException {
  int k = getParameterAsInt(PARAMETER_K);

  List<String> items = new ArrayList<String>();
  Iterator<Example> er = es.iterator();
  while (er.hasNext()) {
    Example ex = er.next();
    items.add(IdUtils.getIdFromExample(ex));
  }

  FlatCrispClusterModel result = new FlatCrispClusterModel();
  for (int i = 0; i < k; i++) result.addCluster(new DefaultCluster("" + i));

  Random rng = RandomGenerator.getRandomGenerator(getParameterAsInt(PARAMETER_LOCAL_RANDOM_SEED));
  for (int i = 0; i < items.size(); i++) {
    int randomIndex = rng.nextInt(k);
    ((DefaultCluster) result.getClusterAt(randomIndex)).addObject(items.get(i));
  }

  return result;
}

@Override
public void run() {
  if (isExpired()) {
    LOG.debug("Schedule " + this + " expired. No need to run.");
    return;
  }

  if (!m_interval.scheduledSuspension()) {
    try {
      org.opennms.ng.services.scheduler.Schedule.this.run();
    } catch (PostponeNecessary e) {
      // Chose a random number of seconds between 5 and 14 to wait before trying again
      m_timer.schedule(random.nextInt(10) * 1000 + 5000, this);
      return;
    }
  }

  // if it is expired by the current run then don't reschedule
  if (isExpired()) {
    LOG.debug("Schedule " + this + " expired. No need to reschedule.");
    return;
  }

  long interval = m_interval.getInterval();
  if (interval >= 0 && m_scheduled) m_timer.schedule(interval, this);
