private String createSuitableLayerName(String name) {

  // Generate a random name if it is null or blank
  if ((name == null) || (name.isEmpty())) {
    Random generator = new Random();
    Integer rand = (Integer) generator.nextInt(10);
    return "Output_" + rand.toString();
  }

  final StringBuilder result = new StringBuilder();
  final StringCharacterIterator iterator = new StringCharacterIterator(name);
  char character = iterator.current();
  while (character != CharacterIterator.DONE) {
    if (character == '.') {
      result.append("_");
    } else if (character == ',') {
      result.append("_");
    } else if (character == '\\') {
      result.append("");
    } else if (character == '?') {
      result.append("");
    } else if (character == '*') {
      result.append("");
    } else if (character == '+') {
      result.append("");
    } else if (character == '&') {
      result.append("");
    } else if (character == ':') {
      result.append("");
    } else if (character == '{') {
      result.append("");
    } else if (character == '}') {
      result.append("");
    } else if (character == '[') {
      result.append("");
    } else if (character == ']') {
      result.append("");
    } else if (character == '(') {
      result.append("");
    } else if (character == ')') {
      result.append("");
    } else if (character == '^') {
      result.append("");
    } else if (character == '$') {
      result.append("");
    } else if (character == ' ') {
      result.append("");
    } else {
      // the char is not a special one
      // add it to the result as is
      result.append(character);
    }
    character = iterator.next();
  }
  return result.toString();
}

private void doLocking(
    InterProcessLock lock,
    AtomicInteger concurrentCount,
    AtomicInteger maxConcurrentCount,
    Random random,
    int maxAllowed)
    throws Exception {
  try {
    Assert.assertTrue(lock.acquire(10, TimeUnit.SECONDS));
    int localConcurrentCount;
    synchronized (this) {
      localConcurrentCount = concurrentCount.incrementAndGet();
      if (localConcurrentCount > maxConcurrentCount.get()) {
        maxConcurrentCount.set(localConcurrentCount);
      }
    }

    Assert.assertTrue(localConcurrentCount <= maxAllowed, "" + localConcurrentCount);

    Thread.sleep(random.nextInt(9) + 1);
  } finally {
    synchronized (this) {
      concurrentCount.decrementAndGet();
      lock.release();
    }
  }
}

static float getFloatSpecial() {
  int i = rng.nextInt();
  int j = rng.nextInt();
  float f = Float.intBitsToFloat(j);
  if (f != f) f = 0; // get rid of NaN for comparison purposes
  if ((i & 0x10) != 0) return f;
  return fspecial[(j & 0x7fffffff) % fspecial.length] * ((i & 0x20) == 0 ? 1 : -1)
      + ((i & 0x03) - 1);
}

