  }
}

/** Given an output filename, write a bunch of random records to it. */
public void map(Text key, Text value, OutputCollector<Text, Text> output, Reporter reporter)
    throws IOException {
  int itemCount = 0;
  while (numBytesToWrite > 0) {
    // Generate the key/value
    int noWordsKey =
        minWordsInKey + (wordsInKeyRange != 0 ? random.nextInt(wordsInKeyRange) : 0);
    int noWordsValue =
        minWordsInValue + (wordsInValueRange != 0 ? random.nextInt(wordsInValueRange) : 0);
    Text keyWords = generateSentence(noWordsKey);
    Text valueWords = generateSentence(noWordsValue);

    // Write the sentence
    output.collect(keyWords, valueWords);

    numBytesToWrite -= (keyWords.getLength() + valueWords.getLength());

    // Update counters, progress etc.
    reporter.incrCounter(
        Counters.BYTES_WRITTEN, (keyWords.getLength() + valueWords.getLength()));
    reporter.incrCounter(Counters.RECORDS_WRITTEN, 1);
    if (++itemCount % 200 == 0) {
      reporter.setStatus("wrote record " + itemCount + ". " + numBytesToWrite + " bytes left.");
    }
  }
  reporter.setStatus("done with " + itemCount + " records.");
}

/**
 * Fills in bases and qualities with a set default quality. If the defaultQuality is set to -1
 * quality scores will be randomly generated. Relies on the alignment start and end having been
 * set to get read length.
 */
private void fillInBasesAndQualities(final SAMRecord rec, final int defaultQuality) {
  final int length = this.readLength;
  final byte[] quals = new byte[length];

  if (-1 != defaultQuality) {
    Arrays.fill(quals, (byte) defaultQuality);
  } else {
    for (int i = 0; i < length; ++i) {
      quals[i] = (byte) this.random.nextInt(50);
    }
  }
  rec.setBaseQualities(quals);
  fillInBases(rec);
}

private void test(boolean random) {
  int len = 2000;
  int[] x = new int[len];
  for (int i = 0; i < len; i++) {
    int key = random ? rand.nextInt() : i;
    x[i] = key;
  }
  IntIntHashMap map = new IntIntHashMap();
  for (int i = 0; i < len; i++) {
    map.put(x[i], i);
  }
  for (int i = 0; i < len; i++) {
    if (map.get(x[i]) != i) {
      throw new AssertionError("get " + x[i] + " = " + map.get(i) + " should be " + i);
    }
  }
  for (int i = 1; i < len; i += 2) {
    map.remove(x[i]);
  }
  for (int i = 1; i < len; i += 2) {
    if (map.get(x[i]) != -1) {
      throw new AssertionError("get " + x[i] + " = " + map.get(i) + " should be <=0");
    }
  }
  for (int i = 1; i < len; i += 2) {
    map.put(x[i], i);
  }
  for (int i = 0; i < len; i++) {
    if (map.get(x[i]) != i) {
      throw new AssertionError("get " + x[i] + " = " + map.get(i) + " should be " + i);
    }
  }
}

