/**
 * Runs through the sequence with one user.
 *
 * @param ahsi Array of script items
 */
private void doSequence(Random r, int iServer) {
  String sOUCU = getNextOUCU();
  try {
    // Random delay before starting (so all threads don't begin in synch)
    if (r != null) Thread.sleep(r.nextInt(DELAYMAX - DELAYMIN + 1) + DELAYMIN);

    Map<String, String> mTokens = new HashMap<String, String>();
    mTokens.put(
        "AUTHCOOKIE",
        "1ed6b71fed0260d1d9aa1730b77325a5430cba4a" + sOUCU + "%2E%2E00000000%2E%2E");
    String sCookie = sendRequestGetCookie(iServer, mTokens, ahsi[0]);
    mTokens.put("SESSIONCOOKIE", sCookie);

    if (DEBUG) System.err.println("* Got cookie: " + sCookie);

    List<StepStats> lSteps;
    synchronized (mStats) {
      lSteps = mStats.get(sOUCU);
      if (lSteps == null) {
        lSteps = new LinkedList<StepStats>();
        mStats.put(sOUCU, lSteps);
      } else throw new Error("wtf?");
      mHosts.put(sOUCU, HOSTS[iServer]);
    }

    for (int iItem = 1; iItem < ahsi.length; iItem++) {
      HttpScript.Item i = ahsi[iItem];
      if (i.getURL().matches("^.*/simple1/(\\?.*)?$")) {
        StepStats ss = new StepStats();
        lSteps.add(ss);

        if (DEBUG) System.err.println("* Step " + (lSteps.size() - 1));
        sendRequestGetMedia(iServer, mTokens, i, ss);

        // Delay
        if (r != null) Thread.sleep(r.nextInt(DELAYMAX - DELAYMIN + 1) + DELAYMIN);
      }
    }

    StringBuffer sb = new StringBuffer(sOUCU + "\t" + HOSTS[iServer] + "\n");
    sb.append("\tXHTML");
    long lTotal = 0;
    for (Iterator i = lSteps.iterator(); i.hasNext(); ) {
      StepStats ss = (StepStats) i.next();
      sb.append("\t" + ss.lTime);
      lTotal += ss.lTime;
    }
    sb.append("\t\t" + lTotal);
    sb.append("\n\tMedia");
    lTotal = 0;
    for (Iterator i = lSteps.iterator(); i.hasNext(); ) {
      StepStats ss = (StepStats) i.next();
      sb.append("\t" + ss.getMediaTime());
      lTotal += ss.getMediaTime();
    }
    sb.append("\t\t" + lTotal);
    System.out.println(sb.toString());
  } catch (Throwable t) {
    System.err.println(sOUCU + "\tError\t" + t);
    synchronized (mStats) {
      mStats.remove(sOUCU);
    }
  }
}

/**
 * Register the FSDataset MBean using the name
 * "hadoop:service=DataNode,name=FSDatasetState-<storageid>"
 */
void registerMBean(final String storageId) {
  // We wrap to bypass standard mbean naming convetion.
  // This wraping can be removed in java 6 as it is more flexible in
  // package naming for mbeans and their impl.
  StandardMBean bean;
  String storageName;
  if (storageId == null || storageId.equals("")) { // Temp fix for the uninitialized storage
    storageName = "UndefinedStorageId" + rand.nextInt();
  } else {
    storageName = storageId;
  }
  try {
    bean = new StandardMBean(this, FSDatasetMBean.class);
    mbeanName = MBeanUtil.registerMBean("DataNode", "FSDatasetState-" + storageName, bean);
    versionBeanName = VersionInfo.registerJMX("DataNode");
  } catch (NotCompliantMBeanException e) {
    e.printStackTrace();
  }

  DataNode.LOG.info("Registered FSDatasetStatusMBean");
}

public static char getRandomChar() {
  Random random = new Random();
  char newChar;
  int next = random.nextInt();
  int type = random.nextInt() % 3;
  int d;
  switch (type) {
    case 0:
      d = next % 10;
      if (d < 0) {
        d = d * (-1);
      }
      newChar = (char) (d + 48);
      break;
    case 1:
      d = next % 26;
      if (d < 0) {
        d = d * (-1);
      }
      newChar = (char) (d + 97);
      break;
    default:
      d = (next % 26);
      if (d < 0) {
        d = d * (-1);
      }
      newChar = (char) (d + 65);
  }
  return newChar;
}

private void evict() {
  while (isFull()) {
    int ptr = rng.nextInt(tableSize);
    Entry old = null;
    int slot = 0;
    for (int b = evictBatch - 1; b >= 0; b--, ptr++) {
      if (tableSize <= ptr) ptr = 0;
      for (Entry e = table.get(ptr); e != null; e = e.next) {
        if (e.dead) continue;
        if (old == null || e.ref.lastAccess < old.ref.lastAccess) {
          old = e;
          slot = ptr;
        }
      }
    }
    if (old != null) {
      old.kill();
      gc();
      final Entry e1 = table.get(slot);
      table.compareAndSet(slot, e1, clean(e1));
    }
  }
}

/**
 * Gets the location of the file to get the audio.
 *
 * @return the file location
 */
public File getSource() {
  File nofile = null;
  // Check if source is a directory
  if (source.exists()) {
    if (source.isDirectory()) {
      // get the list of files in the dir
      String[] entries = source.list();
      Vector files = new Vector();
      for (int i = 0; i < entries.length; i++) {
        File f = new File(source, entries[i]);
        if (f.isFile()) {
          files.addElement(f);
        }
      }
      if (files.size() < 1) {
        throw new BuildException("No files found in directory " + source);
      }
      int numfiles = files.size();
      // get a random number between 0 and the number of files
      Random rn = new Random();
      int x = rn.nextInt(numfiles);
      // set the source to the file at that location
      this.source = (File) files.elementAt(x);
    }
  } else {
    log(source + ": invalid path.", Project.MSG_WARN);
    this.source = nofile;
  }
  return this.source;
}

