      TitanEdge e = v.addEdge("knows", previous);
      e.setProperty("kid", random.nextInt(numV / 2));
    }
    previous = v;
  }
  clopen();

  TestUtil.verifyElementOrder(
      graph.query().orderBy("kid", Order.ASC).limit(500).vertices(), "kid", Order.ASC, 500);
  TestUtil.verifyElementOrder(
      graph.query().orderBy("kid", Order.ASC).limit(300).edges(), "kid", Order.ASC, 300);
  TestUtil.verifyElementOrder(
      graph.query().orderBy("kid", Order.DESC).limit(400).vertices(), "kid", Order.DESC, 400);
  TestUtil.verifyElementOrder(
      graph.query().orderBy("kid", Order.DESC).limit(200).edges(), "kid", Order.DESC, 200);
}

