/**
 * This will unzip the project described by <code>descriptor</code>, open it and refresh the
 * workspace.
 *
 * @param descriptor Description of the project as it should be unzipped.
 * @param monitor {@link IProgressMonitor} that will be used to monitor the operation.
 */
protected void unzipProject(ProjectDescriptor descriptor, IProgressMonitor monitor) {
  final String bundleName = descriptor.getBundleName();
  final String zipLocation = descriptor.getZipLocation();
  final String projectName = descriptor.getProjectName();

  final URL interpreterZipUrl =
      FileLocator.find(Platform.getBundle(bundleName), new Path(zipLocation), null);

  final IProject project = ResourcesPlugin.getWorkspace().getRoot().getProject(projectName);

  if (project.exists()) {
    return;
  }

  try {
    // We make sure that the project is created from this point forward.
    project.create(monitor);

    final ZipInputStream zipFileStream = new ZipInputStream(interpreterZipUrl.openStream());
    ZipEntry zipEntry = zipFileStream.getNextEntry();

    // We derive a regexedProjectName so that the dots don't end up being
    // interpreted as the dot operator in the regular expression language.
    final String regexedProjectName =
        projectName.replaceAll("\\.", "\\."); // $NON-NLS-1$ //$NON-NLS-2$

    while (zipEntry != null) {
      // We will construct the new file but we will strip off the project
      // directory from the beginning of the path because we have already
      // created the destination project for this zip.
      final File file =
          new File(
              project.getLocation().toString(),
              zipEntry
                  .getName()
                  .replaceFirst(
                      "^" + regexedProjectName + "/",
                      "")); // $NON-NLS-1$//$NON-NLS-2$//$NON-NLS-3$

      if (!zipEntry.isDirectory()) {

        /*
         * Copy files (and make sure parent directory exist)
         */
        final File parentFile = file.getParentFile();
        if (null != parentFile && !parentFile.exists()) {
          parentFile.mkdirs();
        }

        OutputStream os = null;

        try {
          os = new FileOutputStream(file);

          final int bufferSize = 102400;
          final byte[] buffer = new byte[bufferSize];
          while (true) {
            final int len = zipFileStream.read(buffer);
            if (zipFileStream.available() == 0) {
              break;
            }
            os.write(buffer, 0, len);
          }
        } finally {
          if (null != os) {
            os.close();
          }
        }
      }

      zipFileStream.closeEntry();
      zipEntry = zipFileStream.getNextEntry();
    }

    project.open(monitor);
    project.refreshLocal(IResource.DEPTH_INFINITE, monitor);

    // Close and re-open the project to force eclipse to re-evaluate
    // any natures that this project may have.
    project.close(monitor);
    project.open(monitor);
  } catch (final IOException e) {
    log(e);
  } catch (final CoreException e) {
    log(e);
  }
}

public static String EscapedDouble(String string) {
  String escaped = string.replaceAll("\'", "\\\\\\'");
  return escaped;
}

protected String escape(String str) {
  if (str == null) return "";
  return str.replaceAll("&", "&amp;").replaceAll("<", "&lt;").replaceAll(">", "&gt;");
}

private Criteria prepareExampleCriteria(
    User exampleUser, String orderByProperty, boolean orderDescending, String... ignoreProperty) {
  Example example = Example.create(exampleUser).enableLike(MatchMode.ANYWHERE).ignoreCase();

  // Sanitize input
  if (orderByProperty != null) {
    orderByProperty = orderByProperty.replaceAll("[^a-zA-Z0-9]", "");
  }

  for (String s : ignoreProperty) example.excludeProperty(s);

  Session session = (Session) entityManager.getDelegate();

  Criteria crit = session.createCriteria(User.class).add(example);
  if (orderByProperty != null)
    crit.addOrder(orderDescending ? Order.desc(orderByProperty) : Order.asc(orderByProperty));

  return crit.setResultTransformer(DistinctRootEntityResultTransformer.INSTANCE);
}

protected static String increment(int count, String template) {
  Pattern p = Pattern.compile("\\$(\\d+)");
  Matcher m = p.matcher(template);
  while (m.find()) {
    int num = Integer.parseInt(m.group(1));
    int newNum = num + count;
    template = template.replaceAll("\\$" + num, "\\$" + newNum);
    // System.out.println(template);
  }
  return template;
}

