static List<String> processLogContent(String logContent) {
  List<String> objects = new ArrayList<String>();
  StringTokenizer tokenizer = new StringTokenizer(logContent, TestReport.TAB, false);
  while (tokenizer.hasMoreTokens()) {
    String token = tokenizer.nextToken();
    if (!(LINE_SEPARATOR.equals(token))) {
      objects.add(token.replaceAll(LINE_SEPARATOR, ""));
    }
  }
  return objects;
}

/**
 * Builds the past-tense form for regular verbs. The rules are performed in this order:
 *
 * <ul>
 *   <li>If the verb is <em>be</em> and the number agreement is plural then the realised form is
 *       <em>were</em>.
 *   <li>If the verb is <em>be</em> and the number agreement is singular then the realised form is
 *       <em>was</em>.
 *   <li>For verbs ending <em>-e</em> the ending becomes <em>-ed</em>. For example,
 *       <em>chased</em> becomes <em>chased</em>.
 *   <li>For verbs ending <em>-Cy</em>, where C is any consonant, the ending becomes
 *       <em>-ied</em>. For example, <em>dry</em> becomes <em>dried</em>.
 *   <li>For every other verb, <em>-ed</em> is added to the end of the word.
 * </ul>
 *
 * @param baseForm the base form of the word.
 * @param number the number agreement for the word.
 * @return the inflected word.
 */
private static String buildRegularPastVerb(String baseForm, Object number) {
  String morphology = null;
  if (baseForm != null) {
    if (baseForm.equalsIgnoreCase("be")) { // $NON-NLS-1$
      if (NumberAgreement.PLURAL.equals(number)) {
        morphology = "were"; // $NON-NLS-1$
      } else {
        morphology = "was"; // $NON-NLS-1$
      }
    } else if (baseForm.endsWith("e")) { // $NON-NLS-1$
      morphology = baseForm + "d"; // $NON-NLS-1$
    } else if (baseForm.matches(".*[b-z&&[^eiou]]y\\b")) { // $NON-NLS-1$
      morphology = baseForm.replaceAll("y\\b", "ied"); // $NON-NLS-1$ //$NON-NLS-2$
    } else {
      morphology = baseForm + "ed"; // $NON-NLS-1$
    }
  }
  return morphology;
}

public String getSelectFieldsSql4(
    String table,
    String fieldList,
    String idField,
    String sortField1,
    String sortField2,
    int begin,
    int end,
    String join,
    String where,
    String order) {
  String sql =
      " select "
          + fieldList.replaceAll(table + "\\.", "TEMP_QUERY.")
          + " from TEMP_QUERY where rank between ? and ? order by ";
  // here, need to replace table name with TEMP_QUERY in 'order'
  String newOrder = String.valueOf(order);
  newOrder = newOrder.replaceAll(table + "\\.", "TEMP_QUERY.");
  sql += newOrder;
  // only add next part if it's not already there
  if (newOrder.indexOf("TEMP_QUERY." + idField) < 0) sql += ",TEMP_QUERY." + idField;

  return sql;
}

public void run(String arg) {
  ImagePlus imp = WindowManager.getCurrentImage();
  if (imp == null || (imp != null && imp.getStackSize() < 2 && !IJ.isMacro())) {
    IJ.error("Stack Writer", "This command requires a stack.");
    return;
  }
  int stackSize = imp.getStackSize();
  String name = imp.getTitle();
  int dotIndex = name.lastIndexOf(".");
  if (dotIndex >= 0) name = name.substring(0, dotIndex);
  hyperstack = imp.isHyperStack();
  LUT[] luts = null;
  int lutIndex = 0;
  int nChannels = imp.getNChannels();
  if (hyperstack) {
    dim = imp.getDimensions();
    if (imp.isComposite()) luts = ((CompositeImage) imp).getLuts();
    if (firstTime && ndigits == 4) {
      ndigits = 3;
      firstTime = false;
    }
  }

  GenericDialog gd = new GenericDialog("Save Image Sequence");
  if (!IJ.isMacro()) fileType = staticFileType;
  gd.addChoice("Format:", choices, fileType);
  gd.addStringField("Name:", name, 12);
  if (!hyperstack) gd.addNumericField("Start At:", startAt, 0);
  gd.addNumericField("Digits (1-8):", ndigits, 0);
  if (!hyperstack) gd.addCheckbox("Use slice labels as file names", useLabels);
  gd.setSmartRecording(true);
  gd.showDialog();
  if (gd.wasCanceled()) return;
  fileType = gd.getNextChoice();
  if (!IJ.isMacro()) staticFileType = fileType;
  name = gd.getNextString();
  if (!hyperstack) startAt = (int) gd.getNextNumber();
  if (startAt < 0) startAt = 0;
  ndigits = (int) gd.getNextNumber();
  if (!hyperstack) useLabels = gd.getNextBoolean();
  else useLabels = false;
  int number = 0;
  if (ndigits < 1) ndigits = 1;
  if (ndigits > 8) ndigits = 8;
  int maxImages = (int) Math.pow(10, ndigits);
  if (stackSize > maxImages && !useLabels && !hyperstack) {
    IJ.error(
        "Stack Writer",
        "More than "
            + ndigits
            + " digits are required to generate \nunique file names for "
            + stackSize
            + " images.");
    return;
  }
  String format = fileType.toLowerCase(Locale.US);
  if (format.equals("fits") && !FileSaver.okForFits(imp)) return;

  if (format.equals("text")) format = "text image";
  String extension = "." + format;
  if (format.equals("tiff")) extension = ".tif";
  else if (format.equals("text image")) extension = ".txt";

  String title = "Save Image Sequence";
  String macroOptions = Macro.getOptions();
  String directory = null;
  if (macroOptions != null) {
    directory = Macro.getValue(macroOptions, title, null);
    if (directory != null) {
      File f = new File(directory);
      boolean exists = f.exists();
      if (directory.indexOf(".") == -1 && !exists) {
        // Is 'directory' a macro variable?
        if (directory.startsWith("&")) directory = directory.substring(1);
        Interpreter interp = Interpreter.getInstance();
        String directory2 = interp != null ? interp.getStringVariable(directory) : null;
        if (directory2 != null) directory = directory2;
      }
      if (!f.isDirectory() && (exists || directory.lastIndexOf(".") > directory.length() - 5))
        directory = f.getParent();
      if (directory != null && !(directory.endsWith(File.separator) || directory.endsWith("/")))
        directory += "/";
    }
  }
  if (directory == null) {
    if (Prefs.useFileChooser && !IJ.isMacOSX()) {
      String digits = getDigits(number);
      SaveDialog sd = new SaveDialog(title, name + digits + extension, extension);
      String name2 = sd.getFileName();
      if (name2 == null) return;
      directory = sd.getDirectory();
    } else directory = IJ.getDirectory(title);
  }
  if (directory == null) return;
  Overlay overlay = imp.getOverlay();
  boolean isOverlay = overlay != null && !imp.getHideOverlay();
  if (!(format.equals("jpeg") || format.equals("png"))) isOverlay = false;
  ImageStack stack = imp.getStack();
  ImagePlus imp2 = new ImagePlus();
  imp2.setTitle(imp.getTitle());
  Calibration cal = imp.getCalibration();
  int nSlices = stack.getSize();
  String path, label = null;
  imp.lock();
  for (int i = 1; i <= nSlices; i++) {
    IJ.showStatus("writing: " + i + "/" + nSlices);
    IJ.showProgress(i, nSlices);
    ImageProcessor ip = stack.getProcessor(i);
    if (isOverlay) {
      imp.setSliceWithoutUpdate(i);
      ip = imp.flatten().getProcessor();
    } else if (luts != null && nChannels > 1 && hyperstack) {
      ip.setColorModel(luts[lutIndex++]);
      if (lutIndex >= luts.length) lutIndex = 0;
    }
    imp2.setProcessor(null, ip);
    String label2 = stack.getSliceLabel(i);
    imp2.setProperty("Label", null);
    if (label2 != null) {
      if (label2.contains("\n")) imp2.setProperty("Info", label2);
      else imp2.setProperty("Label", label2);
      ;
    } else {
      Properties props = imp2.getProperties();
      if (props != null) props.remove("Info");
    }
    imp2.setCalibration(cal);
    String digits = getDigits(number++);
    if (useLabels) {
      label = stack.getShortSliceLabel(i);
      if (label != null && label.equals("")) label = null;
      if (label != null) label = label.replaceAll("/", "-");
    }
    if (label == null) path = directory + name + digits + extension;
    else path = directory + label + extension;
    if (i == 1) {
      File f = new File(path);
      if (f.exists()) {
        if (!IJ.isMacro()
            && !IJ.showMessageWithCancel(
                "Overwrite files?",
                "One or more files will be overwritten if you click \"OK\".\n \n" + path)) {
          imp.unlock();
          IJ.showStatus("");
          IJ.showProgress(1.0);
          return;
        }
      }
    }
    if (Recorder.record) Recorder.disablePathRecording();
    imp2.setOverlay(null);
    if (overlay != null && format.equals("tiff")) {
      Overlay overlay2 = overlay.duplicate();
      overlay2.crop(i, i);
      if (overlay2.size() > 0) {
        for (int j = 0; j < overlay2.size(); j++) {
          Roi roi = overlay2.get(j);
          int pos = roi.getPosition();
          if (pos == 1) roi.setPosition(i);
        }
        imp2.setOverlay(overlay2);
      }
    }
    IJ.saveAs(imp2, format, path);
  }
  imp.unlock();
  if (isOverlay) imp.setSlice(1);
  IJ.showStatus("");
}

public static String humanize(String property) {
  String s = property.replaceAll("(?=\\p{Upper})", " ");
  return s.substring(0, 1).toUpperCase() + s.substring(1);
}

public void checkIndex(String arrayIndex, int line) {
  // check if ID is a variable of type int
  arrayIndex = arrayIndex.replaceAll(" ", "");
  if (varList.containsKey(arrayIndex) && varList.get(arrayIndex).equals("int")) {
    if (debugging) {
      System.out.println(arrayIndex + "\n" + varList.get(arrayIndex));
    }
