public int debug(Interpreter interp, int mode) {
  if (IJ.debugMode) IJ.log("debug: " + interp.getLineNumber() + " " + mode + " " + interp);
  if (mode == Interpreter.RUN) return 0;
  if (!isVisible()) { // abort macro if user closes window
    interp.abortMacro();
    return 0;
  }
  if (!isActive()) toFront();
  int n = interp.getLineNumber();
  if (n == previousLine) {
    previousLine = 0;
    return 0;
  }
  previousLine = n;
  if (mode == Interpreter.RUN_TO_CARET) {
    if (n == runToLine) {
      mode = Interpreter.STEP;
      interp.setDebugMode(mode);
    } else return 0;
  }
  String text = ta.getText();
  if (IJ.isWindows() && !IJ.isVista()) text = text.replaceAll("\r\n", "\n");
  char[] chars = new char[text.length()];
  chars = text.toCharArray();
  int count = 1;
  debugStart = 0;
  int len = chars.length;
  debugEnd = len;
  for (int i = 0; i < len; i++) {
    if (chars[i] == '\n') count++;
    if (count == n && debugStart == 0) debugStart = i + 1;
    else if (count == n + 1) {
      debugEnd = i;
      break;
    }
  }
  // IJ.log("debug: "+debugStart+" "+debugEnd+" "+len+" "+count);
  if (debugStart == 1) debugStart = 0;
  if ((debugStart == 0 || debugStart == len) && debugEnd == len)
    return 0; // skip code added with Interpreter.setAdditionalFunctions()
  ta.select(debugStart, debugEnd);
  if (debugWindow != null && !debugWindow.isShowing()) {
    interp.setEditor(null);
    debugWindow = null;
  } else debugWindow = interp.updateDebugWindow(interp.getVariables(), debugWindow);
  if (mode == Interpreter.STEP) {
    step = false;
    while (!step && !interp.done() && isVisible()) IJ.wait(5);
  } else {
    if (mode == Interpreter.FAST_TRACE) IJ.wait(5);
    else IJ.wait(150);
  }
  return 0;
}

/**
 * Initalize the loader with a configuration.
 *
 * @param numberOfFormulasByBDD Number of formulas that will be used in each BDD.
 * @param numberOfFormulas Number of formulas that will be loaded.
 * @return List of formulas for each BDD.
 */
public ArrayList<String> getFormulas(int numberOfFormulasByBDD, int numberOfFormulas) {
  numVariables = -1;
  numClausules = numberOfFormulas;
  this.variables = new ArrayList<String>();
  this.named_variables = new ArrayList<String>();
  this.integer_variables = new ArrayList<String>();
  this.formulas = new ArrayList<String>();
  this.original_formulas = new ArrayList<String>();
  try {
    // Open the file that is the first
    // command line parameter
    FileInputStream fstream = new FileInputStream(filename);
    // Get the object of DataInputStream
    DataInputStream in = new DataInputStream(fstream);
    BufferedReader br = new BufferedReader(new InputStreamReader(in));
    String line;

    int formulaIndex = 1;
    if (VERBOSE) System.out.println("Extracting clausules");
    // Read File Line By Line
    while ((line = br.readLine()) != null && line.length() > 0) {
      if (line.length() == 0) break;
      // Asume that lines starting with # area comments
      // (not defined in that way by S. She)
      if (line.charAt(0) != '#') {
        // Boolean named variable
        if (line.charAt(0) == '@') {
          String[] content = line.split(" ");
          String variable = START_VAR + content[1] + END_VAR;
          variables.add(variable);
          named_variables.add(variable);
          numVariables++;
          // System.out.println(variable);
        }
        // Integer variable? x_i
        else if (line.charAt(0) == '$') {
          String[] content = line.split(" ");
          String variable = START_VAR + content[1] + END_VAR;
          variables.add(variable);
          integer_variables.add(variable);
          numVariables++;
          // System.out.println(variable);
        }
        // Clausules
        else {
          String formula = line.trim();
          original_formulas.add(formula);

          // Okay, do not touch this. This regular expressions have been
          // carved in stone gotten of the Mount Doom
          // No, seriously, DO NOT TOUCH THEM, they work
          String formulaI = formula;
          formulaI = formulaI.replace("|", " || ");
          formulaI = formulaI.replace("!!", " ");
          formulaI = formulaI.replace("&", " && ");
          formulaI =
              formulaI.replaceAll(
                  "([^\\w_]+)(x[1-9]+)([^\\w_]+)", "$1 " + START_VAR + "$2" + END_VAR + "$3");
          formulaI = formulaI.replaceAll("(\\!?)([\\w_\\d]+)", "$1" + START_VAR + "$2" + END_VAR);
          formulaI = formulaI.replace(START_VAR + START_VAR, START_VAR);
          formulaI = formulaI.replace(END_VAR + END_VAR, END_VAR);
          formulaI = formulaI.replace(START_VAR + "0" + END_VAR, " false ");
          formulaI = formulaI.replace(START_VAR + "1" + END_VAR, " true ");
          formulaI = formulaI.replace(START_VAR + "false" + END_VAR, " false ");
          formulaI = formulaI.replace(START_VAR + "true" + END_VAR, " true ");
          formulaI =
              formulaI.replaceAll(
                  Pattern.quote(START_VAR)
                      + "([\\w\\d_]+)"
                      + Pattern.quote(END_VAR)
                      + "\\s*\\|\\|\\s*true\\s*",
                  "true");
          formulaI = formulaI.replace("<=>", "<->");

          formulas.add(formulaI);

          // Warn the user everything runs smoothly
          if (VERBOSE) {
            System.out.println(
                "Extracting clausule: "
                    + (formulaIndex)
                    + " / "
                    + numClausules
                    + ": "
                    + formulaI);
          }

          formulaIndex++;

          // Stop condition, yeah there are other ways to do this
          // but I prefer this one
          if (formulaIndex > numClausules
              && numClausules != -1
              && numClausules != FileLoaderConfiguration.ALL_CLAUSULES) break;
        }
      }
    }

    // Close the input stream
    in.close();

  } catch (Exception e) { // Catch exception if any
    System.err.println("Error: " + e.getMessage());
    e.printStackTrace();
  }

  if (this.reordering == SheFileLoader.REORDER_BY_OCURRENCE) {
    variables = this.getOrderedVariablesByOcurrence();
  }

  // Initialize variables with a given order
  BDD.init(variables);

  // Now we are going to create the bdd one clausule at a time
  bdd_formulas = new ArrayList<String>(formulas.size() / numberOfFormulasByBDD);
  String formulaBDDI = "";
  boolean firstIteration = true;
  for (int i = 0; i < formulas.size(); i++) {
    if (!firstIteration) formulaBDDI += "&& (" + formulas.get(i) + ") ";
    else {
      formulaBDDI += "(" + formulas.get(i) + ") ";
      firstIteration = false;
    }
    if ((i + 1) % numberOfFormulasByBDD == 0) {
      bdd_formulas.add(formulaBDDI);
      formulaBDDI = "";
      firstIteration = true;
    }
  }
  return bdd_formulas;
}

