private static String updateUrls(
    String folder, String themeName, boolean inheritedFile, String strLine) {
  // Define image url prefix
  String urlPrefix = "";
  if (inheritedFile) {
    urlPrefix = "../" + themeName + "/";
  }

  if (strLine.indexOf("url(/") > 0) {
    // Do nothing for urls beginning with /
  } else if (strLine.indexOf("url(../") >= 0) {
    // eliminate a path segment in the folder name for every
    // "../"
    String[] folderSegments = folder.split("/");
    int segmentCount = folderSegments.length;
    while (segmentCount > 0 && strLine.indexOf("url(../") >= 0) {
      segmentCount--;
      strLine = strLine.replaceAll("url\\(../", ("url\\("));
    }
    // add remaining path segments to urlPrefix
    StringBuilder sb = new StringBuilder(urlPrefix);
    for (int i = 0; i < segmentCount; i++) {
      sb.append(folderSegments[i]);
      sb.append("/");
    }
    strLine = strLine.replaceAll("url\\(", ("url\\(" + sb.toString()));

  } else {
    strLine = strLine.replaceAll("url\\(", ("url\\(" + urlPrefix + folder + "/"));
  }
  return strLine;
}

/**
 * This method assumes the dateFormat has a separator char in it, and that we can use that to
 * determine what the user entered by using that separator to split up the user entered date, and
 * then do some logic on it. This is by no means a foolproof method and should not be relied upon
 * returning 100% correct dates all the time.
 *
 * @param str String to parse
 * @param dateFormat DateFormat to use
 * @param separators Separator chars that can be encountered
 * @param locale Locale
 * @return Calendar
 * @throws CalendarDateParseException If date could not be parsed
 * @throws Exception If any step of the parsing failed
 */
public static Calendar slashParse(
    final String str, final String dateFormat, final char[] separators, final Locale locale)
    throws CalendarDateParseException, Exception {
  int start = -1;
  String splitter = null;
  String dateFormatToUse = dateFormat;
  for (int i = 0; i < separators.length; i++) {
    start = str.indexOf(separators[i]);
    if (start != -1) {
      splitter = String.valueOf(separators[i]);
      break;
    }
  }
  if (start == -1)
    throw new CalendarDateParseException(
        "Failed to find splitter char", CalendarDateParseException.TYPE_NO_SLPITTER_CHAR);

  // replace dateFormat until we have same splitter
  for (int i = 0; i < separators.length; i++) {
    if (String.valueOf(separators[i]).equals(splitter)) continue;

    dateFormatToUse = dateFormatToUse.replaceAll("\\" + String.valueOf(separators[i]), splitter);
  }

  Calendar toReturn = Calendar.getInstance(locale);
  StringTokenizer st = new StringTokenizer(str, splitter);
  StringTokenizer st2 = new StringTokenizer(dateFormatToUse, splitter);

  if (st.countTokens() != st2.countTokens())
    throw new CalendarDateParseException(
        "Date format does not match date string in terms of splitter character numbers",
        CalendarDateParseException.TYPE_INSUFFICIENT_SPLITTERS);

  // variables we'll be extracting
  int monthToSet = -1;
  int dayToSet = -1;
  int yearToSet = -1;

  // reset, skipping month this time
  st = new StringTokenizer(str, splitter);
  st2 = new StringTokenizer(dateFormatToUse, splitter);

  while (st.hasMoreTokens()) {
    String dateValue = st.nextToken();
    String dateType = st2.nextToken();

    dateValue = dateValue.replaceAll(" ", "");
    dateType = dateType.replaceAll(" ", "");

    int calType = getCalendarTypeForString(dateType);
    // we already did month
    if (calType == Calendar.MONTH) {
      monthToSet = Integer.parseInt(dateValue);
      continue;
    }
    if (calType == Calendar.YEAR) {
      yearToSet = Integer.parseInt(dateValue);
      continue;
    }
    if (calType == Calendar.DATE) {
      dayToSet = Integer.parseInt(dateValue);
      continue;
    }

    toReturn.set(calType, Integer.parseInt(dateValue));
  }

  // set all date parameters at the same time, or else we'll get month-skipping due to setting a
  // value later (such as a date that is too high
  // for the current month). (-1 for month as Calendar class is month-zero-based).
  if (monthToSet != -1 && dayToSet != -1 && yearToSet != -1) {
    toReturn.set(yearToSet, monthToSet - 1, dayToSet);
  } else {
    // set what we know
    if (yearToSet != -1) {
      toReturn.set(Calendar.YEAR, yearToSet);
    }
    if (monthToSet != -1) {
      toReturn.set(Calendar.MONTH, monthToSet - 1);
    }
    if (dayToSet != -1) {
      toReturn.set(Calendar.DATE, dayToSet);
    }
  }

  if (toReturn.get(Calendar.YEAR) < 100)
    toReturn.set(Calendar.YEAR, toReturn.get(Calendar.YEAR) + 1900);

  toReturn.set(Calendar.HOUR_OF_DAY, 0);
  toReturn.set(Calendar.MINUTE, 0);
  toReturn.set(Calendar.SECOND, 0);
  toReturn.set(Calendar.MILLISECOND, 0);

  return toReturn;
}

