/**
 * Fixes missing data on the basis of the startDatePattern field, according to the inference rules
 * prescribed by the specifications (missing data must be inferred by the start date).
 *
 * @return true if a fix was needed
 * @throws RecurrencePatternException if no automatic fix was possible
 */
public boolean fix() throws RecurrencePatternException {

  boolean fixed = false;
  String localStartDate;

  try {
    if (timeZone == null) {
      localStartDate = startDatePattern;
    } else {
      localStartDate =
          TimeUtils.convertUTCDateToLocal(startDatePattern, TimeZone.getTimeZone(timeZone));
    }
  } catch (Exception e) {
    throw new RecurrencePatternException("Conversion of start date pattern to local time failed");
  }
  String yyyyMMddStartDatePattern = localStartDate.replaceAll("-", "");

  try {

    switch (frequency) {
      case TYPE_DAILY:
        // Does nothing
        break;

      case TYPE_YEAR_NTH:
        short month = getMonth(yyyyMMddStartDatePattern);
        if (month != monthOfYear) {
          month = monthOfYear;
          fixed = true;
        }
        // Falls through

      case TYPE_MONTH_NTH:
        if (instance != getBackwardWeekDayInstance(yyyyMMddStartDatePattern)) {
          short startWeekDayInstance = getWeekDayInstance(yyyyMMddStartDatePattern);
          if (startWeekDayInstance != instance) {
            instance = startWeekDayInstance;
            fixed = true;
          }
        }
        // Falls through

      case TYPE_WEEKLY:
        byte startWeekDay = getWeekDay(yyyyMMddStartDatePattern);
        if (!isDayInMask(startWeekDay, dayOfWeekMask)) {
          dayOfWeekMask += startWeekDay;
          fixed = true;
        }
        break;

      case TYPE_YEARLY:
        month = getMonth(yyyyMMddStartDatePattern);
        if (month != monthOfYear) {
          monthOfYear = month;
          fixed = true;
        }
        // Falls through

      case TYPE_MONTHLY:
        if (dayOfMonth != getBackwardMonthDay(yyyyMMddStartDatePattern)) {
          short startMonthDay = getMonthDay(yyyyMMddStartDatePattern);
          if (startMonthDay != dayOfMonth) {
            dayOfMonth = startMonthDay;
            fixed = true;
          }
        }
        break;

      default:
        throw new RecurrencePatternException("Type " + frequency + "not implemented");
    }

  } catch (Exception e) {
    throw new RecurrencePatternException("RecurrencePattern: Format " + "error found. " + e);
  }

  return fixed;
}

protected String getFormatedNode(String document) {
  int offset = getOffset(document);
  Node insertNode = getEditNode(offset, document);
  String text = ReWriteVisitor.createCodeFromNode(insertNode, document, getFormatHelper());
  if (doFormat) {
    text = HsrFormatter.format(document, text, offset);
  }
  if (doTrim) {
    text = text.trim();
  }
  String lineDelimiter = FileHelper.getLineDelimiter(document);
  return text.replaceAll("\n", lineDelimiter);
}

private void handlePackageNotes(
    RollbackPrintWriter out,
    String qname,
    boolean error,
    boolean warn,
    boolean info,
    boolean debug,
    boolean trace) {
  String title = pnotes.title();

  boolean isSubsystem = qname.replaceAll("[^\\.]", "").length() == 2;

  if (!title.isEmpty()) {
    block(title, out, isSubsystem);
  }

  handleNote(out, error, pnotes.error(), qname, "ERROR");
  handleNote(out, warn, pnotes.warn(), qname, "WARN");
  handleNote(out, info, pnotes.info(), qname, "INFO");
  handleNote(out, debug, pnotes.debug(), qname, "DEBUG");
  handleNote(out, trace, pnotes.trace(), qname, "TRACE");
}

