          null,
          null);
    } else {
      for (Iterator i = solutions.iterator(); i.hasNext(); ) {
        Solution solution = (Solution) i.next();
        if (committedOnly && !solution.isCommited().booleanValue()) continue;
        String ownerName = solution.getOwner().getAbbv();
        String onClick =
            "onClick=\"document.location='listSolutions.do?op=Select&id="
                + solution.getUniqueId()
                + "';\"";
        String note = solution.getNote();
        if (note != null) note = note.replaceAll("\n", "<br>");
        PropertiesInfo globalInfo = (PropertiesInfo) solution.getInfo("GlobalInfo");
        String assigned =
            (globalInfo == null ? "?" : globalInfo.getProperty("Assigned variables", "N/A"));
        String totVal =
            (globalInfo == null ? "?" : globalInfo.getProperty("Overall solution value", "N/A"));
        String timePr =
            (globalInfo == null ? "?" : globalInfo.getProperty("Time preferences", "N/A"));
        String studConf =
            (globalInfo == null ? "?" : globalInfo.getProperty("Student conflicts", "N/A"));
        String roomPr =
            (globalInfo == null ? "?" : globalInfo.getProperty("Room preferences", "N/A"));
        String distPr =
            (globalInfo == null
                ? "?"
                : globalInfo.getProperty("Distribution preferences", "N/A"));
        String instrPr =
            (globalInfo == null
                ? "?"
                : globalInfo.getProperty("Back-to-back instructor preferences", "N/A"));
        String tooBig =
            (globalInfo == null ? "?" : globalInfo.getProperty("Too big rooms", "N/A"));
        String useless =
            (globalInfo == null ? "?" : globalInfo.getProperty("Useless half-hours", "N/A"));
        String pertPen =
            (globalInfo == null
                ? "?"
                : globalInfo.getProperty("Perturbations: Total penalty", "N/A"));
        assigned = assigned.replaceAll(" of ", "/");
        if (!"N/A".equals(timePr) && timePr.indexOf('/') >= 0)
          timePr = timePr.substring(0, timePr.indexOf('/')).trim();
        if (!"N/A".equals(roomPr) && roomPr.indexOf('/') >= 0)
          roomPr = roomPr.substring(0, roomPr.indexOf('/')).trim();
        if (!"N/A".equals(instrPr) && instrPr.indexOf('/') >= 0)
          instrPr = instrPr.substring(0, instrPr.indexOf('/')).trim();
        if (!"N/A".equals(assigned) && assigned.indexOf(' ') >= 0)
          assigned = assigned.substring(0, assigned.indexOf(' ')).trim();
        if (!"N/A".equals(timePr) && timePr.indexOf(' ') >= 0)
          timePr = timePr.substring(0, timePr.indexOf(' ')).trim();
        if (!"N/A".equals(roomPr) && roomPr.indexOf(' ') >= 0)
          roomPr = roomPr.substring(0, roomPr.indexOf(' ')).trim();
        if (!"N/A".equals(instrPr) && instrPr.indexOf(' ') >= 0)
          instrPr = instrPr.substring(0, instrPr.indexOf(' ')).trim();
        if (!"N/A".equals(distPr) && distPr.indexOf(' ') >= 0)
          distPr = distPr.substring(0, distPr.indexOf(' ')).trim();
        if (!"N/A".equals(tooBig) && tooBig.indexOf(' ') >= 0)
          tooBig = tooBig.substring(0, tooBig.indexOf(' ')).trim();
        if (!"N/A".equals(useless) && useless.indexOf(' ') >= 0)
          useless = useless.substring(0, useless.indexOf(' ')).trim();
