                .replaceAll("\\]", ")")
                .replaceAll(", ", ",")
                .replaceAll("hard:", "h")
                .replaceAll("distance:", "d")
                .replaceAll("commited:", "c")
                .replaceAll("committed:", "c");
        String settings = null;
        String type = null;
        for (Iterator j = solution.getParameters().iterator(); j.hasNext(); ) {
          SolverParameter p = (SolverParameter) j.next();
          if ("General.SettingsId".equals(p.getDefinition().getName())) {
            SolverPredefinedSetting set =
                (new SolverPredefinedSettingDAO()).get(Long.valueOf(p.getValue()), hibSession);
            if (set != null) settings = set.getDescription();
          }
          if ("Basic.Mode".equals(p.getDefinition().getName())) {
            type = p.getValue();
          }
        }
        type = (settings == null ? type == null ? "?" : type : settings);

        String bgColor = null;
        if (myForm.getSolutionBean(solution.getUniqueId()) != null) bgColor = "rgb(168,187,225)";

        webTable
            .addLine(
                onClick,
                new String[] {
                  sDF.format(new Date(solution.getCreated().getTime())),
                  type,
                  (solution.isValid().booleanValue()
                      ? "<IMG border='0' align='absmiddle' src='images/tick.gif'>"
                      : ""),
                  (solution.isCommited().booleanValue()
                      ? sDF.format(new Date(solution.getCommitDate().getTime()))
                      : ""),
                  ownerName,
                  assigned,
                  totVal,
                  timePr,
                  studConf,
                  roomPr,
                  distPr,
                  instrPr,
                  tooBig,
                  useless,
                  pertPen,
                  note
                },
                new Comparable[] {
                  solution.getCreated(),
                  type,
                  (solution.isValid().booleanValue() ? new Integer(1) : new Integer(0)),
                  (solution.isCommited().booleanValue()
                      ? new Long(solution.getCommitDate().getTime())
                      : new Long(0)),
                  ownerName,
                  assigned,
                  totVal,
                  timePr,
                  studConf,
                  roomPr,
                  distPr,
                  instrPr,
                  tooBig,
                  useless,
                  pertPen,
                  (solution.getNote() == null ? "" : solution.getNote())
                })
            .setBgColor(bgColor);
        nrLines++;
      }
      if (nrLines == 0)
        webTable.addLine(
            null,
            new String[] {
              "<i>No solution saved by "
                  + Web.getUser(request.getSession()).getName()
                  + " so far.</i>"
            },
            null,
            null);
    }
    request.setAttribute(
        "ListSolutions.table",
        webTable.printTable(WebTable.getOrder(request.getSession(), "listSolutions.ord")));

  } catch (Exception e) {
    e.printStackTrace();
  }
}

