/**
 * Checks that the path only contains the system path separator. If not, wrong ones are replaced.
 */
private static String adjustSeparator(String path) {
  String sp = File.separator;
  if ("\\".equals(sp)) sp = "\\\\";
  return path.replaceAll("/", sp);
}

public static String unquote(final String value) {
  String result = null;
  if (value != null) {
    result = value.replaceAll("\"(.*)\"", "$1");
  }
  return result;
}

/**
 * Creates an equation from text (LaTeX-like) format.
 *
 * @param sEquation Equation text
 * @param f ItemFactory to use
 * @param fZoom Zoom factor (1.0 = default)
 * @return Equation object
 * @throws EquationFormatException If there's anything wrong with the input
 */
public static Equation create(String sEquation, ItemFactory f, float fZoom)
    throws EquationFormatException {
  try {
    // Ensure equation is a single line (makes it easier to show errors)
    sEquation = sEquation.replaceAll("[\r\n]", " ").trim();
    EquationFormat ef = new EquationFormat(new StringReader(sEquation));
    return create(ef.equation().createDOM(XML.createDocument()), f, fZoom);
  } catch (ParseException pe) {
    throw new EquationFormatException(pe, sEquation);
  }
}

private static short instanceModifierToInt(String modifier) { // Irrespective of the
  if (modifier.indexOf("-") != -1) { // version
    return (short) -Short.parseShort(modifier.replaceAll("\\-", ""));
  }
  return Short.parseShort(modifier.replaceAll("\\+", ""));
}

