    boolean hasTitle,
    View buttonPanel) {
  int fullDark =
      a.getResourceId(R.styleable.AlertDialog_fullDark, R.drawable.dialog_full_holo_dark);
  int topDark = a.getResourceId(R.styleable.AlertDialog_topDark, R.drawable.dialog_top_holo_dark);
  int centerDark =
      a.getResourceId(R.styleable.AlertDialog_centerDark, R.drawable.dialog_middle_holo_dark);
  int bottomDark =
      a.getResourceId(R.styleable.AlertDialog_bottomDark, R.drawable.dialog_bottom_holo_dark);
  int fullBright =
      a.getResourceId(R.styleable.AlertDialog_fullBright, R.drawable.dialog_full_holo_dark);
  int topBright =
      a.getResourceId(R.styleable.AlertDialog_topBright, R.drawable.dialog_top_holo_dark);
  int centerBright =
      a.getResourceId(R.styleable.AlertDialog_centerBright, R.drawable.dialog_middle_holo_dark);
  int bottomBright =
      a.getResourceId(R.styleable.AlertDialog_bottomBright, R.drawable.dialog_bottom_holo_dark);
  int bottomMedium =
      a.getResourceId(R.styleable.AlertDialog_bottomMedium, R.drawable.dialog_bottom_holo_dark);
  View[] views = new View[4];
  boolean[] light = new boolean[4];
  View lastView = null;
  boolean lastLight = false;

  int pos = 0;
  if (hasTitle) {
    views[pos] = topPanel;
    light[pos] = false;
    pos++;
  }
  views[pos] = contentPanel.getVisibility() == View.GONE ? null : contentPanel;
  light[pos] = mListView != null;
  pos++;
  if (customPanel != null) {
    views[pos] = customPanel;
    light[pos] = mForceInverseBackground;
    pos++;
