private void setBackground(
    LinearLayout topPanel,
    LinearLayout contentPanel,
    View customPanel,
    boolean hasButtons,
    TypedArray a,
    boolean hasTitle,
    View buttonPanel) {

  /* Get all the different background required */
  int fullDark = a.getResourceId(R.styleable.AlertDialog_fullDark, R.drawable.popup_full_dark);
  int topDark = a.getResourceId(R.styleable.AlertDialog_topDark, R.drawable.popup_top_dark);
  int centerDark =
      a.getResourceId(R.styleable.AlertDialog_centerDark, R.drawable.popup_center_dark);
  int bottomDark =
      a.getResourceId(R.styleable.AlertDialog_bottomDark, R.drawable.popup_bottom_dark);
  int fullBright =
      a.getResourceId(R.styleable.AlertDialog_fullBright, R.drawable.popup_full_bright);
  int topBright = a.getResourceId(R.styleable.AlertDialog_topBright, R.drawable.popup_top_bright);
  int centerBright =
      a.getResourceId(R.styleable.AlertDialog_centerBright, R.drawable.popup_center_bright);
  int bottomBright =
      a.getResourceId(R.styleable.AlertDialog_bottomBright, R.drawable.popup_bottom_bright);
  int bottomMedium =
      a.getResourceId(R.styleable.AlertDialog_bottomMedium, R.drawable.popup_bottom_medium);

  /*
   * We now set the background of all of the sections of the alert.
   * First collect together each section that is being displayed along
   * with whether it is on a light or dark background, then run through
   * them setting their backgrounds. This is complicated because we need
   * to correctly use the full, top, middle, and bottom graphics depending
   * on how many views they are and where they appear.
   */

  View[] views = new View[4];
  boolean[] light = new boolean[4];
  View lastView = null;
  boolean lastLight = false;

  int pos = 0;
  if (hasTitle) {
    views[pos] = topPanel;
    light[pos] = false;
    pos++;
  }

  /* The contentPanel displays either a custom text message or
   * a ListView. If it's text we should use the dark background
   * for ListView we should use the light background. If neither
   * are there the contentPanel will be hidden so set it as null.
   */
  views[pos] = (contentPanel.getVisibility() == View.GONE) ? null : contentPanel;
  light[pos] = mListView != null;
  pos++;
  if (customPanel != null) {
    views[pos] = customPanel;
    light[pos] = mForceInverseBackground;
    pos++;
  }
  if (hasButtons) {
    views[pos] = buttonPanel;
    light[pos] = true;
  }

  boolean setView = false;
  for (pos = 0; pos < views.length; pos++) {
    View v = views[pos];
    if (v == null) {
      continue;
    }
    if (lastView != null) {
      if (!setView) {
        lastView.setBackgroundResource(lastLight ? topBright : topDark);
      } else {
        lastView.setBackgroundResource(lastLight ? centerBright : centerDark);
      }
      setView = true;
    }
    lastView = v;
    lastLight = light[pos];
  }

  if (lastView != null) {
    if (setView) {

      /* ListViews will use the Bright background but buttons use
       * the Medium background.
       */
      lastView.setBackgroundResource(
          lastLight ? (hasButtons ? bottomMedium : bottomBright) : bottomDark);
    } else {
      lastView.setBackgroundResource(lastLight ? fullBright : fullDark);
    }
  }

  /* TODO: uncomment section below. The logic for this should be if
   * it's a Contextual menu being displayed AND only a Cancel button
   * is shown then do this.
   */
  // if (hasButtons && (mListView != null)) {

  /* Yet another *special* case. If there is a ListView with buttons
   * don't put the buttons on the bottom but instead put them in the
   * footer of the ListView this will allow more items to be
   * displayed.
   */

  /*
  contentPanel.setBackgroundResource(bottomBright);
  buttonPanel.setBackgroundResource(centerMedium);
  ViewGroup parent = (ViewGroup) mWindow.findViewById(R.id.parentPanel);
  parent.removeView(buttonPanel);
  AbsListView.LayoutParams params = new AbsListView.LayoutParams(
  AbsListView.LayoutParams.MATCH_PARENT,
  AbsListView.LayoutParams.MATCH_PARENT);
  buttonPanel.setLayoutParams(params);
  mListView.addFooterView(buttonPanel);
  */
  // }

  if ((mListView != null) && (mAdapter != null)) {
    mListView.setAdapter(mAdapter);
    if (mCheckedItem > -1) {
      mListView.setItemChecked(mCheckedItem, true);
      mListView.setSelection(mCheckedItem);
    }
  }
}

public static AlertDialog listViewDialog(
    final Context context,
    final int titleResId,
    final ListAdapter adapter,
    final DialogInterface.OnClickListener yesAction,
    final DialogInterface.OnClickListener noAction) {
  final View layout = View.inflate(context, R.layout.listdialog, null);
  final ListView listView = ((ListView) layout.findViewById(R.id.list_view));
  listView.setAdapter(adapter);

  final AlertDialog.Builder builder = newBuilder(context);
  if (titleResId != -1) builder.setTitle(titleResId);
  if (yesAction != null) builder.setPositiveButton("OK", yesAction);

  final DialogInterface.OnClickListener noActionListener =
      noAction != null ? noAction : MessageBox.NoAction;
  builder.setNegativeButton("Cancel", noActionListener);
  builder.setOnCancelListener(
      new DialogInterface.OnCancelListener() {
        @Override
        public void onCancel(DialogInterface dialog) {
          noActionListener.onClick(dialog, -1);
        }
      });

  builder.setView(layout);

  return show(builder);
}

