private void setBackground(
    LinearLayout topPanel,
    LinearLayout contentPanel,
    View customPanel,
    boolean hasButtons,
    TypedArray a,
    boolean hasTitle,
    View buttonPanel) {
  int fullDark =
      a.getResourceId(R.styleable.AlertDialog_fullDark, R.drawable.dialog_full_holo_dark);
  int topDark = a.getResourceId(R.styleable.AlertDialog_topDark, R.drawable.dialog_top_holo_dark);
  int centerDark =
      a.getResourceId(R.styleable.AlertDialog_centerDark, R.drawable.dialog_middle_holo_dark);
  int bottomDark =
      a.getResourceId(R.styleable.AlertDialog_bottomDark, R.drawable.dialog_bottom_holo_dark);
  int fullBright =
      a.getResourceId(R.styleable.AlertDialog_fullBright, R.drawable.dialog_full_holo_dark);
  int topBright =
      a.getResourceId(R.styleable.AlertDialog_topBright, R.drawable.dialog_top_holo_dark);
  int centerBright =
      a.getResourceId(R.styleable.AlertDialog_centerBright, R.drawable.dialog_middle_holo_dark);
  int bottomBright =
      a.getResourceId(R.styleable.AlertDialog_bottomBright, R.drawable.dialog_bottom_holo_dark);
  int bottomMedium =
      a.getResourceId(R.styleable.AlertDialog_bottomMedium, R.drawable.dialog_bottom_holo_dark);
  View[] views = new View[4];
  boolean[] light = new boolean[4];
  View lastView = null;
  boolean lastLight = false;

  int pos = 0;
  if (hasTitle) {
    views[pos] = topPanel;
    light[pos] = false;
    pos++;
  }
  views[pos] = contentPanel.getVisibility() == View.GONE ? null : contentPanel;
  light[pos] = mListView != null;
  pos++;
  if (customPanel != null) {
    views[pos] = customPanel;
    light[pos] = mForceInverseBackground;
    pos++;
  }
  if (hasButtons) {
    views[pos] = buttonPanel;
    light[pos] = true;
  }
  boolean setView = false;
  for (pos = 0; pos < views.length; pos++) {
    View v = views[pos];
    if (v == null) {
      continue;
    }
    if (lastView != null) {
      if (!setView) {
        lastView.setBackgroundResource(lastLight ? topBright : topDark);
      } else {
        lastView.setBackgroundResource(lastLight ? centerBright : centerDark);
      }
      setView = true;
    }
    lastView = v;
    lastLight = light[pos];
  }
  if (lastView != null) {
    if (setView) {
      lastView.setBackgroundResource(
          lastLight ? hasButtons ? bottomMedium : bottomBright : bottomDark);
    } else {
      lastView.setBackgroundResource(lastLight ? fullBright : fullDark);
    }
  }
  if (mListView != null && mAdapter != null) {
    mListView.setAdapter(mAdapter);
    if (mCheckedItem > -1) {
      mListView.setItemChecked(mCheckedItem, true);
      mListView.setSelection(mCheckedItem);
    }
  }
}

