  // set adapter
  ListAdapter adapter = new ListAdapter(mAct, mContactChecked);
  mListView.setAdapter(adapter);
  mListView.setOnItemClickListener(adapter);

  // restore
  mListView.setSelectionFromTop(index, top);
}

