public static void main(String args[]) {
  JFrame frame = new JFrame("Visualization Test");
  frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);

  Game theGame = GameRepository.getDefaultRepository().getGame("biddingTicTacToe");
  VisualizationPanel theVisual = new VisualizationPanel(theGame);
  frame.setPreferredSize(new Dimension(1200, 900));
  frame.getContentPane().add(theVisual);
  frame.pack();
  frame.setVisible(true);

  StateMachine theMachine = new ProverStateMachine();
  theMachine.initialize(theGame.getRules());
  try {
    MachineState theCurrentState = theMachine.getInitialState();
    do {
      theVisual.observe(new ServerNewGameStateEvent(theCurrentState));
      theCurrentState = theMachine.getRandomNextState(theCurrentState);
      Thread.sleep(2750);
      System.out.println("State: " + theCurrentState);
    } while (!theMachine.isTerminal(theCurrentState));
    theVisual.observe(new ServerNewGameStateEvent(theCurrentState));
  } catch (Exception e) {
    e.printStackTrace();
  }
}

public void run() {
  if (this.infos == null) {
    this.infos = UIManager.getInstalledLookAndFeels();
    Thread.currentThread().setUncaughtExceptionHandler(this);
    JPanel panel = new JPanel();
    panel.setBorder(this);
    this.frame = new JFrame(getClass().getSimpleName());
    this.frame.add(panel);
    this.frame.setDefaultCloseOperation(JFrame.DISPOSE_ON_CLOSE);
    this.frame.setVisible(true);
  }
  if (this.index == this.infos.length) {
    this.frame.dispose();
  } else {
    LookAndFeelInfo info = this.infos[this.index % this.infos.length];
    try {
      UIManager.setLookAndFeel(info.getClassName());
    } catch (Exception exception) {
      System.err.println("could not change look and feel");
    }
    SwingUtilities.updateComponentTreeUI(this.frame);
    this.frame.pack();
    this.frame.setLocationRelativeTo(null);
    this.index++;
    SwingUtilities.invokeLater(this);
  }
}

