  GridBagConstraints gBC = new GridBagConstraints();
  gBC.fill = GridBagConstraints.HORIZONTAL;
  gBC.weightx = .75;
  gBC.gridx = 0;
  gBC.gridy = 0;
  JLabel heading1 = new JLabel("<html><h1>ORDER</h1><hr></html>");
  JLabel heading2 = new JLabel("<html><h1>COMPLETE?</h1><hr></html>");
  heading1.setForeground(Color.white);
  heading2.setForeground(Color.white);

  desktop.add(heading1, gBC);
  gBC.gridx = 1;
  gBC.weightx = .25;
  desktop.add(heading2, gBC);
  gBC.gridx = 0;
  gBC.weightx = .75;

  //////// adding all the orders to the screen /////////
  DirectoryScanner scanner = new DirectoryScanner();
  scanner.setIncludes(new String[] {"**/*.POS"});
  scanner.setBasedir(".");
  scanner.setCaseSensitive(false);
  scanner.scan();
  final String[] files = scanner.getIncludedFiles();
  for (int i = 0; i < files.length; i++) {
    BufferedReader in;
    String order = "<html><h2>";
    try {
      in = new BufferedReader(new FileReader(files[i]));
      order += in.readLine() + "</h2><table>";
      String line = in.readLine();
      int lineNum = 1;
      while (line != null) {
        if (lineNum != 1) {
          order += "<tr>";
          if (lineNum == 2) order += "Phone: ";
          order += line + "</tr>";
        }
        lineNum++;
        line = in.readLine();
      }
      order += "</table></html>";
      in.close();
    } catch (Exception e) {
      e.printStackTrace();
    }
    JLabel o = new JLabel(order);
    o.setForeground(Color.white);
    gBC.gridy++;
    desktop.add(o, gBC);
  }
  /////////////////////////////////////////////////////////

  ///// adding buttons for marking orders as complete ////
  gBC.gridy = 0;
  gBC.gridx = 1;
  gBC.weightx = .25;
  for (int i = 0; i < files.length; i++) {
    JButton done = new JButton("Done");
    done.setPreferredSize(new Dimension(100, 75));
    final int k = i;
    done.addActionListener(
        new ActionListener() {
          public void actionPerformed(ActionEvent e) {
            // on button press...
            File f = new File(files[k]);
            boolean success = f.delete();
            if (!success) throw new IllegalArgumentException("Delete: deletion failed");
            else restartProgram(args);
          }
        });

    gBC.gridy++;
    desktop.add(done, gBC);
  }
  ////////////////////////////////////////////////////////

  // Display the window.
  frame.pack();
  frame.setVisible(true);
}

