public static void startGUI() {
  final JFrame f =
      new JFrame("J2DBench") {
        public Dimension getPreferredSize() {
          Dimension pref = super.getPreferredSize();
          pref.width = Math.max(pref.width, 800);
          pref.height = Math.max(pref.height, 600);
          return pref;
        }
      };
  guiFrame = f;
  f.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
  f.getContentPane().setLayout(new BorderLayout());
  f.getContentPane().add(Group.root.getJComponent(), BorderLayout.CENTER);
  JPanel p = new JPanel();
  p.setLayout(new BoxLayout(p, BoxLayout.X_AXIS));
  JButton b = new JButton("Run Tests...");
  b.addActionListener(
      new ActionListener() {
        public void actionPerformed(ActionEvent e) {
          if (!saveOrDiscardLastResults()) {
            return;
          }
          if (verbose.isEnabled()) {
            System.out.println(e);
            System.out.println("running tests...");
          }
          new Thread(
                  new Runnable() {
                    public void run() {
                      runTests(true);
                    }
                  })
              .start();
          if (verbose.isEnabled()) {
            System.out.println("done");
          }
        }
      });
  p.add(b);

  b = new JButton("Load Options");
  b.addActionListener(
      new ActionListener() {
        public void actionPerformed(ActionEvent e) {
          JFileChooser fc = getFileChooser();
          int ret = fc.showOpenDialog(f);
          if (ret == JFileChooser.APPROVE_OPTION) {
            String reason = loadOptions(fc.getSelectedFile());
            if (reason != null) {
              System.err.println(reason);
            }
          }
        }
      });
  p.add(b);

  b = new JButton("Save Options");
  b.addActionListener(
      new ActionListener() {
        public void actionPerformed(ActionEvent e) {
          JFileChooser fc = getFileChooser();
          int ret = fc.showSaveDialog(f);
          if (ret == JFileChooser.APPROVE_OPTION) {
            String reason = saveOptions(fc.getSelectedFile());
            if (reason != null) {
              System.err.println(reason);
            }
          }
        }
      });
  p.add(b);

  b = new JButton("Save Results");
  b.addActionListener(
      new ActionListener() {
        public void actionPerformed(ActionEvent e) {
          if (saveResults()) {
            lastResults = null;
          }
        }
      });
  p.add(b);

  b = new JButton("Quit");
  b.addActionListener(
      new ActionListener() {
        public void actionPerformed(ActionEvent e) {
          if (!saveOrDiscardLastResults()) {
            return;
          }
          System.exit(0);
        }
      });
  p.add(b);

  f.getContentPane().add(p, BorderLayout.SOUTH);
  f.pack();
  f.show();
}

