public Map<String, UUID> call() throws Exception {
  Map<String, UUID> uuidMap = new HashMap<String, UUID>();
  int requests = (int) Math.ceil(names.size() / PROFILES_PER_REQUEST);
  for (int i = 0; i < requests; i++) {
    HttpURLConnection connection = createConnection();
    String body =
        JSONArray.toJSONString(names.subList(i * 100, Math.min((i + 1) * 100, names.size())));
    writeBody(connection, body);
    JSONArray array =
        (JSONArray) jsonParser.parse(new InputStreamReader(connection.getInputStream()));
    for (Object profile : array) {
      JSONObject jsonProfile = (JSONObject) profile;
      String id = (String) jsonProfile.get("id");
      String name = (String) jsonProfile.get("name");
      UUID uuid = UUIDFetcher.getUUID(id);
      uuidMap.put(name, uuid);
    }
    if (rateLimiting && i != requests - 1) {
      Thread.sleep(100L);
    }
  }
  return uuidMap;
}

/** acquireSharedTimed times out if not released before timeout */
public void testAcquireSharedNanos_Timeout() {
  final BooleanLatch l = new BooleanLatch();
  Thread t =
      new Thread(
          new Runnable() {
            public void run() {
              try {
                threadAssertFalse(l.isSignalled());
                threadAssertFalse(l.tryAcquireSharedNanos(0, SMALL_DELAY_MS * 1000 * 1000));
              } catch (InterruptedException ie) {
                threadUnexpectedException();
              }
            }
          });
  t.start();
  try {
    Thread.sleep(SHORT_DELAY_MS);
    assertFalse(l.isSignalled());
    t.join();
  } catch (InterruptedException e) {
    unexpectedException();
  }
}

