public void generateLoad() throws Exception {
  LineNumberReader in = new LineNumberReader(new InputStreamReader(System.in));

  System.err.print("server[localhost]: ");
  String t = in.readLine().trim();
  if (t.length() == 0) t = "localhost";
  String host = t;

  System.err.print("port[8080]: ");
  t = in.readLine().trim();
  if (t.length() == 0) t = "8080";
  int port = Integer.parseInt(t);

  System.err.print("context[/cometd]: ");
  t = in.readLine().trim();
  if (t.length() == 0) t = "/cometd";
  String uri = t + "/cometd";

  address = new Address(host, port);

  int nclients = 100;
  int size = 50;
  int rooms = 100;
  int rooms_per_client = 1;
  int publish = 1000;
  int pause = 100;
  int burst = 10;
  int maxLatency = 5000;

  System.err.print("base[/chat/demo]: ");
  t = in.readLine().trim();
  if (t.length() == 0) t = "/chat/demo";
  String base = t;

  System.err.print("rooms [" + rooms + "]: ");
  t = in.readLine().trim();
  if (t.length() == 0) t = "" + rooms;
  rooms = Integer.parseInt(t);

  System.err.print("rooms per client [" + rooms_per_client + "]: ");
  t = in.readLine().trim();
  if (t.length() == 0) t = "" + rooms_per_client;
  rooms_per_client = Integer.parseInt(t);

  System.err.print("max Latency [" + maxLatency + "]: ");
  t = in.readLine().trim();
  if (t.length() == 0) t = "" + maxLatency;
  maxLatency = Integer.parseInt(t);

  while (true) {
    System.err.println("--");

    System.err.print("clients [" + nclients + "]: ");
    t = in.readLine().trim();
    if (t.length() == 0) t = "" + nclients;
    nclients = Integer.parseInt(t);

    if (nclients < rooms || (nclients % rooms) != 0) {
      System.err.println("Clients must be a multiple of " + rooms);
      nclients = (nclients / rooms) * rooms;
      continue;
    }

    while (clients.size() < nclients) {
      int u = clients.size();
      BayeuxClient client =
          new BayeuxClient(http, address, uri) {
            public void deliver(Client from, Message message) {
              if (Bayeux.META_SUBSCRIBE.equals(message.get(Bayeux.CHANNEL_FIELD))
                  && ((Boolean) message.get(Bayeux.SUCCESSFUL_FIELD)).booleanValue())
                _subscribed.incrementAndGet();
              super.deliver(from, message);
            }
          };

      MessageListener listener =
          new MessageListener() {
            public void deliver(Client fromClient, Client toClient, Message msg) {
              Object data = (Object) msg.get(AbstractBayeux.DATA_FIELD);
              if (data != null) {
                String msgId = (String) msg.get(AbstractBayeux.ID_FIELD);
                // System.err.println(name+": "+data);
                if (msgId != null) {
                  long latency = System.currentTimeMillis() - Long.parseLong(msgId);
                  synchronized (BayeuxLoadGenerator.this) {
                    _got++;
                    if (_maxLatency < latency) _maxLatency = latency;
                    if (_minLatency == 0 || latency < _minLatency) _minLatency = latency;
                    _totalLatency += latency;
                  }
                }
              }
            }
          };
      client.addListener(listener);

      client.start();

      clients.add(client);
      if (clients.size() % 10 == 0) {
        int i = clients.size();
        System.err.println(
            "clients = " + (i >= 1000 ? "" : i >= 100 ? "0" : i >= 10 ? "00" : "000") + i);
        Thread.sleep(300);
      }

      client.startBatch();
      if (rooms_per_client == 1) {
        int room = u % rooms;
        client.subscribe(room > 0 ? (base + "/" + room) : base);
      } else {
        for (int i = 0; i < rooms_per_client; i++) {
          int room = _random.nextInt(rooms);
          client.subscribe(room > 0 ? (base + "/" + room) : base);
        }
      }
      client.endBatch();
    }

    while (clients.size() > nclients) {
      BayeuxClient client = clients.remove(0);
      client.remove(false);
      _subscribed.addAndGet(-rooms_per_client);
      if (clients.size() % 10 == 0) {
        int i = clients.size();
        System.err.println(
            "clients = " + (i >= 1000 ? "" : i >= 100 ? "0" : i >= 10 ? "00" : "000") + i);
        Thread.sleep(300);
      }
    }

    Thread.sleep(500);

    while (_subscribed.get() != nclients * rooms_per_client) {
      // System.err.println(destination.toDetailString());
      System.err.println(
          "Subscribed:" + _subscribed.get() + " != " + (nclients * rooms_per_client) + " ...");
      Thread.sleep(1000);
    }

    System.err.println("Clients: " + nclients + " subscribed:" + _subscribed.get());

    synchronized (this) {
      _got = 0;
      _minLatency = 0;
      _maxLatency = 0;
      _totalLatency = 0;
    }

    System.err.print("publish [" + publish + "]: ");
    t = in.readLine().trim();
    if (t.length() == 0) t = "" + publish;
    publish = Integer.parseInt(t);

    System.err.print("publish size [" + size + "]: ");
    t = in.readLine().trim();
    if (t.length() == 0) t = "" + size;
    size = Integer.parseInt(t);
    String chat = "";
    for (int i = 0; i < size; i++) chat += "x";

    System.err.print("pause [" + pause + "]: ");
    t = in.readLine().trim();
    if (t.length() == 0) t = "" + pause;
    pause = Integer.parseInt(t);

    System.err.print("batch [" + burst + "]: ");
    t = in.readLine().trim();
    if (t.length() == 0) t = "" + burst;
    burst = Integer.parseInt(t);

    long start = System.currentTimeMillis();
    trial:
    for (int i = 1; i <= publish; ) {
      // System.err.print(i);
      // System.err.print(',');
      int u = _random.nextInt(nclients);
      BayeuxClient c = clients.get(u);
      final String name =
          "Client" + (u >= 1000 ? "" : u >= 100 ? "0" : u >= 10 ? "00" : "000") + u;
      Object msg =
          new JSON.Literal("{\"user\":\"" + name + "\",\"chat\":\"" + chat + " " + i + "\"}");
      c.startBatch();
      for (int b = 0; b < burst; b++) {
        int room = _random.nextInt(rooms);
        String id = "" + System.currentTimeMillis();
        c.publish(room > 0 ? (base + "/" + room) : base, msg, id);
        i++;

        if (i % 10 == 0) {
          long latency = 0;
          synchronized (this) {
            if (_got > 0) latency = _totalLatency / _got;
          }
          if (latency > maxLatency) {
            System.err.println("\nABORTED!");
            break trial;
          }

          char dot = (char) ('0' + (int) (latency / 100));
          System.err.print(dot);
          if (i % 1000 == 0) System.err.println();
        }
      }
      c.endBatch();

      if (pause > 0) Thread.sleep(pause);
    }

    Thread.sleep(_maxLatency);

    for (BayeuxClient c : clients) {
      if (!c.isPolling()) System.err.println("PROBLEM WITH " + c);
    }

    System.err.println();

    long last = 0;
    int sleep = 100;
    while (_got < (nclients / rooms * rooms_per_client * publish)) {
      System.err.println(
          "Got:" + _got + " < " + (nclients / rooms * rooms_per_client * publish) + " ...");
      Thread.sleep(sleep);
      if (last != 0 && _got == last) break;
      last = _got;
      sleep += 100;
    }
    System.err.println("Got:" + _got + " of " + (nclients / rooms * rooms_per_client * publish));

    long end = System.currentTimeMillis();

    System.err.println(
        "Got "
            + _got
            + " at "
            + (_got * 1000 / (end - start))
            + "/s, latency min/ave/max ="
            + _minLatency
            + "/"
            + (_totalLatency / _got)
            + "/"
            + _maxLatency
            + "ms");
  }
}

/** await returns after countDown to zero, but not before */
public void testAwait() {
  final CountDownLatch l = new CountDownLatch(2);

  Thread t =
      new Thread(
          new Runnable() {
            public void run() {
              try {
                threadAssertTrue(l.getCount() > 0);
                l.await();
                threadAssertTrue(l.getCount() == 0);
              } catch (InterruptedException e) {
                threadUnexpectedException();
              }
            }
          });
  t.start();
  try {
    assertEquals(l.getCount(), 2);
    Thread.sleep(SHORT_DELAY_MS);
    l.countDown();
    assertEquals(l.getCount(), 1);
    l.countDown();
    assertEquals(l.getCount(), 0);
    t.join();
  } catch (InterruptedException e) {
    unexpectedException();
  }
}

